-- 94. Old Cuban


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('683baefe-2105-4355-9dee-e35c50988dd3', 'old-cuban', 'c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8', '{"en":"Old Cuban"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","rum","champagne","mint","elegant","modern"}', '{"en":"An elegant marriage of Mojito and French 75, the Old Cuban combines aged rum with fresh mint, lime, and Champagne. This sophisticated cocktail showcases the evolution of classic Cuban cocktail culture."}'::jsonb, '{"created_year":"2001","origin":{"city":"New York City","bar":"Pegu Club","country":"USA"},"creator":{"name":"Audrey Saunders","profession":"bartender"},"story":{"en":"Created in 2001 by renowned bartender Audrey Saunders at the Pegu Club in New York City. Saunders sought to create a more sophisticated version of the Mojito by combining it with elements of the French 75. The result was a refined, effervescent cocktail that quickly became a modern classic. The drink represents the new wave of craft cocktail innovation while honoring Cuban cocktail traditions."},"named_after":{"en":"Named \"Old Cuban\" to evoke the classic Cuban cocktail tradition while showcasing a modern interpretation."}}'::jsonb, '{"profile":["sparkling","minty","sophisticated"],"description":{"en":"Refreshing mint and lime with the richness of aged rum, elevated by Champagne bubbles and a hint of Angostura. Complex, elegant, and perfectly balanced."},"first_impression":{"en":"Fresh mint and sparkling effervescence with aged rum depth"},"finish":{"en":"Long, elegant finish with lingering mint and rum complexity"},"balance":{"en":"Perfectly balanced between fresh mint, citrus, aged rum richness, and Champagne elegance"}}'::jsonb, '{"best_time":["evening","afternoon"],"occasions":["celebration","date_night","special_occasion","aperitivo"],"seasons":["summer","spring"],"food_pairings":{"en":"Excellent with oysters, seafood appetizers, Cuban cuisine, or as an elegant aperitif before fine dining."},"ideal_for":{"en":"Perfect for those who love Mojitos but want something more refined. Ideal for Champagne and rum enthusiasts seeking sophisticated cocktails."}}'::jsonb, '[{"ingredient_id":"ING_RUM_AGED","quantity":{"amount":45,"unit":"ml"},"display_name":"Aged rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":25,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":30,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_MINT_LEAVES","quantity":{"amount":6,"unit":"leaves"},"display_name":"Fresh mint leaves","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":2,"unit":"dashes"},"display_name":"Angostura bitters","notes":null},{"ingredient_id":"ING_CHAMPAGNE","quantity":{"amount":60,"unit":"ml"},"display_name":"Champagne","notes":null}]'::jsonb, 'shake', '{"en":"Muddle mint leaves gently with simple syrup in a shaker. Add rum, lime juice, bitters, and ice. Shake well. Double strain into a chilled cocktail glass. Top with Champagne."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Mint sprig"}'::jsonb, 'none', 'up', '{"ING_RUM_AGED"}', '{"sparkling","minty","sophisticated"}', 14, 200, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring"}', '{"celebration","date_night","special_occasion"}', false, false, '{}', 'Muddle mint gently to avoid bitterness. Use aged rum for complexity. Add Champagne last to preserve bubbles. Double strain to remove mint particles.', 'high', 70, 'https://iba-world.com/old-cuban/', 'IBA Official Recipe', 1)
