-- 95. Paloma


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('3b56676c-66f5-4ea1-ae8e-81d01ef117a8', 'paloma', 'a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2', '{"en":"Paloma"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","tequila","grapefruit","mexican","refreshing"}', '{"en":"Mexico''s most popular tequila cocktail, the Paloma combines tequila with fresh grapefruit and lime, topped with soda. Simple, refreshing, and perfectly balanced between sweet, sour, and bitter."}'::jsonb, '{"created_year":"1950","origin":{"city":"Unknown","bar":"Unknown","country":"Mexico"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Paloma''s exact origins are unclear, though it likely emerged in Mexico in the 1950s. Some attribute it to Don Javier Delgado Corona, owner of La Capilla bar in Tequila, Mexico. The name \"Paloma\" means \"dove\" in Spanish. Despite being less known internationally than the Margarita, the Paloma is actually Mexico''s most popular tequila cocktail. Its simple, refreshing nature made it a favorite throughout Mexico, particularly during hot weather."},"named_after":{"en":"Named \"Paloma\" (dove in Spanish), possibly after a popular Mexican folk song \"La Paloma.\""}}'::jsonb, '{"profile":["grapefruit","refreshing","balanced"],"description":{"en":"Bright grapefruit with tequila warmth, lime tartness, and sparkling soda. The natural bitterness of grapefruit balances the sweetness perfectly. Refreshing and easy-drinking."},"first_impression":{"en":"Bright grapefruit and lime with tequila backbone"},"finish":{"en":"Clean, refreshing finish with lingering grapefruit bitterness"},"balance":{"en":"Perfectly balanced between grapefruit bitterness, citrus tartness, and gentle sweetness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["casual","beach","party","mexican_dinner"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with Mexican food, tacos, ceviche, grilled fish, or spicy dishes."},"ideal_for":{"en":"Perfect for those who find Margaritas too sweet or want a more refreshing tequila cocktail. Ideal for hot weather and casual gatherings."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_BLANCO","quantity":{"amount":50,"unit":"ml"},"display_name":"Blanco tequila","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":10,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_SODA","quantity":{"amount":100,"unit":"ml"},"display_name":"Grapefruit soda","notes":null}]'::jsonb, 'build', '{"en":"Pour tequila and lime juice into a highball glass filled with ice. Top with grapefruit soda. Stir gently. Optional: rim with salt."}'::jsonb, 'Highball glass', '{"en":"Grapefruit wedge (optional salt rim)"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_TEQUILA_BLANCO"}', '{"grapefruit","refreshing","balanced"}', 10, 150, 'easy', 30, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","beach","party"}', false, false, '{"paloma-with-fresh-grapefruit"}', 'Can use fresh grapefruit juice + soda water instead of grapefruit soda for a more refined version. Salt rim is traditional but optional.', 'low', 90, 'https://iba-world.com/paloma/', 'IBA Official Recipe', 1)
