-- 98. Pisco Punch


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('eedad787-2dd1-4ebe-b351-8de088a233f4', 'pisco-punch', 'e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2c3d4e5f6', '{"en":"Pisco Punch"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","pisco","pineapple","historical","punch"}', '{"en":"A legendary San Francisco punch combining Pisco with pineapple, citrus, and aromatic bitters. This historic cocktail was the drink of choice during the California Gold Rush era."}'::jsonb, '{"created_year":"1878","origin":{"city":"San Francisco","bar":"Bank Exchange Saloon","country":"USA"},"creator":{"name":"Duncan Nicol","profession":"bartender"},"story":{"en":"Created around 1878 by Duncan Nicol at the Bank Exchange Saloon in San Francisco. The drink became legendary during the Gold Rush era and was considered one of San Francisco''s most famous cocktails. Mark Twain and other literary figures were known to frequent the Bank Exchange specifically for this punch. The original recipe was a closely guarded secret, and when Nicol died in 1926, the exact formula was lost. Modern versions are reconstructions based on historical accounts and tasting notes."},"named_after":{"en":"Named after Pisco, the Peruvian/Chilean grape brandy that forms its base."}}'::jsonb, '{"profile":["tropical","citrus","complex"],"description":{"en":"Tropical pineapple sweetness with Pisco''s grape brandy character, bright citrus notes, and aromatic complexity. Smooth, fruity, and deceptively strong."},"first_impression":{"en":"Tropical pineapple with bright citrus and Pisco smoothness"},"finish":{"en":"Long, smooth finish with lingering tropical fruit and grape notes"},"balance":{"en":"Well-balanced between tropical sweetness, citrus brightness, and Pisco strength"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["party","celebration","historical_tasting","summer_gathering"],"seasons":["summer","spring"],"food_pairings":{"en":"Excellent with Peruvian ceviche, tropical fruit platters, or light seafood dishes."},"ideal_for":{"en":"Perfect for Pisco enthusiasts and cocktail history buffs. Ideal for those seeking a taste of San Francisco''s legendary drinking culture."}}'::jsonb, '[{"ingredient_id":"ING_PISCO","quantity":{"amount":60,"unit":"ml"},"display_name":"Pisco","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'shake', '{"en":"Shake all ingredients with ice. Strain into a rocks glass filled with ice."}'::jsonb, 'Rocks glass', '{"en":"Pineapple wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_PISCO"}', '{"tropical","citrus","complex"}', 18, 180, 'easy', 45, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring"}', '{"party","celebration","historical_tasting"}', false, false, '{"original-pisco-punch"}', 'The original recipe was lost, this is a modern reconstruction. Use quality Pisco for best results. Deceptively strong - warn guests.', 'mid', 60, 'https://iba-world.com/pisco-punch/', 'IBA Official Recipe - Modern reconstruction of historic recipe', 1)
