-- 99. Porn Star Martini


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('ee666e40-8e54-402d-859c-592c8564a9fb', 'porn-star-martini', 'd6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2c3d4e5', '{"en":"Porn Star Martini"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","vodka","passion-fruit","glamorous","modern"}', '{"en":"A bold, glamorous cocktail combining vodka with passion fruit, vanilla, and a shot of Champagne on the side. This modern classic is sweet, fruity, and unapologetically indulgent."}'::jsonb, '{"created_year":"2002","origin":{"city":"London","bar":"Maverick Bar","country":"UK"},"creator":{"name":"Douglas Ankrah","profession":"bartender"},"story":{"en":"Created in 2002 by Douglas Ankrah at the Maverick Bar in London. Ankrah invented the drink after visiting a gentlemen''s club in Cape Town called the Maverick Bar, which inspired both the drink and his London venue. The provocative name was chosen to reflect the drink''s bold, glamorous nature. Despite (or perhaps because of) its controversial name, the cocktail became enormously popular, particularly in the UK, and is now one of the best-selling cocktails worldwide."},"named_after":{"en":"Provocatively named to evoke glamour and indulgence, inspired by the strip clubs Ankrah visited."}}'::jsonb, '{"profile":["fruity","sweet","tropical"],"description":{"en":"Sweet, tropical passion fruit with vanilla smoothness and vodka backbone. The Champagne sidecar adds elegance and effervescence. Rich, fruity, and indulgent."},"first_impression":{"en":"Sweet passion fruit and vanilla with vodka smoothness"},"finish":{"en":"Sweet, fruity finish with lingering vanilla and tropical notes"},"balance":{"en":"Sweet and fruity with balanced acidity from passion fruit"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","girls_night","birthday"],"seasons":["all_seasons"],"food_pairings":{"en":"Excellent with tropical fruit platters, white chocolate desserts, or as a celebratory aperitif."},"ideal_for":{"en":"Perfect for those who love sweet, fruity cocktails with a touch of glamour. Ideal for celebrations or anyone seeking an indulgent, show-stopping drink."}}'::jsonb, '[{"ingredient_id":"ING_VODKA_VANILLA","quantity":{"amount":60,"unit":"ml"},"display_name":"Vanilla vodka","notes":null},{"ingredient_id":"ING_PASSION_FRUIT_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Passion fruit liqueur","notes":null},{"ingredient_id":"ING_PASSION_FRUIT_PUREE","quantity":{"amount":30,"unit":"ml"},"display_name":"Passion fruit purée","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_CHAMPAGNE","quantity":{"amount":60,"unit":"ml"},"display_name":"Champagne (served on the side)","notes":null}]'::jsonb, 'shake', '{"en":"Shake vodka, passion fruit liqueur, passion fruit purée, lime juice, and simple syrup with ice. Strain into a chilled coupe glass. Serve with a shot of Champagne on the side."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Half passion fruit"}'::jsonb, 'none', 'up', '{"ING_VODKA_VANILLA"}', '{"fruity","sweet","tropical"}', 16, 220, 'medium', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-season"}', '{"party","celebration","birthday"}', false, false, '{"passion-star-martini"}', 'Serve Champagne shot on the side - guest can sip alternately or pour into the cocktail. Use fresh passion fruit purée for best results. Garnish with half passion fruit floating on top.', 'high', 95, 'https://iba-world.com/porn-star-martini/', 'IBA Official Recipe', 1)
