-- 100. Russian Spring Punch


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b6bf9530-1569-44a7-99de-d4bf627a7cc1', 'russian-spring-punch', 'e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0', '{"en":"Russian Spring Punch"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","vodka","champagne","berry","sparkling"}', '{"en":"A sophisticated vodka-based punch combining fresh lemon, crème de cassis, and Champagne. This elegant sparkling cocktail features beautiful berry flavors and a refreshing effervescence."}'::jsonb, '{"created_year":"1984","origin":{"city":"London","bar":"Trader Vic''s","country":"UK"},"creator":{"name":"Dick Bradsell","profession":"bartender"},"story":{"en":"Created by legendary bartender Dick Bradsell in 1984 at Trader Vic''s in London. Bradsell wanted to create a sophisticated vodka cocktail with Champagne that would appeal to elegant drinkers. The drink combines the strength of vodka with the elegance of Champagne and the fruity depth of cassis. It became one of Bradsell''s signature creations and helped establish his reputation as one of the most influential bartenders of the modern cocktail era."},"named_after":{"en":"Named \"Russian\" for vodka, \"Spring\" for its fresh, seasonal character, and \"Punch\" for its complex, multi-ingredient nature."}}'::jsonb, '{"profile":["sparkling","fruity","elegant"],"description":{"en":"Bright lemon tartness balanced with sweet-tart cassis, vodka smoothness, and Champagne elegance. Complex berry flavors with refreshing bubbles."},"first_impression":{"en":"Bright citrus and berry with sparkling effervescence"},"finish":{"en":"Long, elegant finish with lingering berry and citrus notes"},"balance":{"en":"Perfectly balanced between citrus tartness, berry sweetness, and Champagne elegance"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["celebration","brunch","garden_party","date_night"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with berry desserts, light seafood, smoked salmon canapés, or as a celebratory aperitif."},"ideal_for":{"en":"Perfect for vodka and Champagne lovers seeking an elegant, fruity cocktail. Ideal for celebrations or sophisticated brunches."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":25,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":25,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_CREME_DE_CASSIS","quantity":{"amount":15,"unit":"ml"},"display_name":"Crème de cassis","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_CHAMPAGNE","quantity":{"amount":90,"unit":"ml"},"display_name":"Champagne","notes":null}]'::jsonb, 'shake', '{"en":"Shake vodka, lemon juice, crème de cassis, and simple syrup with ice. Strain into a highball glass filled with ice. Top with Champagne."}'::jsonb, 'Highball glass', '{"en":"Fresh berries and lemon slice"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"sparkling","fruity","elegant"}', 12, 190, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"celebration","brunch","garden_party"}', false, false, '{}', 'Add Champagne last to preserve carbonation. Fresh berries enhance presentation. Can substitute Prosecco for Champagne.', 'high', 65, 'https://iba-world.com/russian-spring-punch/', 'IBA Official Recipe', 1)
