-- 103. Spicy Fifty


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f822b10e-0973-4316-af4e-422a7bd2fe4d', 'spicy-fifty', 'a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0b1', '{"en":"Spicy Fifty"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","vodka","spicy","sophisticated","elegant"}', '{"en":"An elegant and spicy modern cocktail featuring vanilla vodka, elderflower liqueur, and fresh chili pepper. This sophisticated creation balances floral sweetness with a subtle heat, creating a refined drinking experience that showcases the versatility of contemporary mixology."}'::jsonb, '{"created_year":"2009","origin":{"city":"London","bar":"Skybar at Trader Vic''s","country":"United Kingdom"},"creator":{"name":"Salvatore Calabrese","profession":"bartender"},"story":{"en":"Created by legendary bartender Salvatore Calabrese at the Skybar at Trader Vic''s in London in 2009 to celebrate his 50th birthday. The \"Fifty\" in the name refers to both his milestone birthday and the drink''s elegant sophistication. Calabrese wanted to create something that reflected his passion for balance and innovation, combining unexpected elements like vanilla, elderflower, and chili. The cocktail won multiple awards and became a modern classic, showcasing how spice can be incorporated elegantly into cocktails."},"named_after":{"en":"Named \"Fifty\" to celebrate Salvatore Calabrese''s 50th birthday and the drink''s sophisticated elegance befitting a milestone celebration."}}'::jsonb, '{"profile":["spicy","floral","citrus","sophisticated"],"description":{"en":"Beautifully layered with vanilla vodka''s smoothness, elderflower''s delicate floral notes, bright citrus from lemon and grapefruit, and a gentle warming heat from fresh chili. The spice builds gradually without overwhelming, creating an elegant complexity."},"first_impression":{"en":"Floral elderflower and citrus with vanilla smoothness, followed by gentle heat"},"finish":{"en":"Lingering warmth with floral and citrus notes, gentle spicy tingle"},"balance":{"en":"Perfectly balanced between sweet floral notes, tart citrus, vanilla smoothness, and controlled heat"}}'::jsonb, '{"best_time":["evening","aperitif","late_night"],"occasions":["aperitivo","date_night","celebration","cocktail_party"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with spicy Asian cuisine, ceviche, sushi, grilled seafood, and Thai dishes. Also pairs well with fresh oysters."},"ideal_for":{"en":"Perfect for adventurous drinkers who appreciate sophisticated spice in cocktails. Ideal for those seeking an elegant aperitif with a twist. Great for impressing guests with a modern classic."}}'::jsonb, '[{"ingredient_id":"ING_VODKA_VANILLA","quantity":{"amount":50,"unit":"ml"},"display_name":"Vanilla vodka","notes":null},{"ingredient_id":"ING_ELDERFLOWER_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Elderflower liqueur","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Honey syrup","notes":null},{"ingredient_id":"ING_RED_CHILI_PEPPER","quantity":{"amount":1,"unit":"slice"},"display_name":"Fresh red chili pepper (1 slice)","notes":null}]'::jsonb, 'shake', '{"en":"Muddle one slice of red chili pepper in a cocktail shaker (adjust quantity for desired heat level). Add vanilla vodka, elderflower liqueur, lemon juice, honey syrup, and ice. Shake vigorously until well-chilled. Double strain into a chilled Martini glass. Float a thin slice of red chili on top as garnish."}'::jsonb, 'Martini glass (Cocktail)', '{"en":"Thin slice of fresh red chili pepper (floating)"}'::jsonb, 'none', 'up', '{"ING_VODKA_VANILLA"}', '{"spicy","floral","citrus","sophisticated"}', 17, 170, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","date_night","celebration"}', false, false, '{"extra-spicy-fifty","spicy-fifty-on-rocks"}', 'Adjust chili quantity based on guest preference - one slice is moderate. St-Germain is standard elderflower liqueur. Honey syrup is 1:1 honey to water. Double strain to remove chili seeds.', 'mid', 75, 'https://iba-world.com/spicy-fifty/', 'IBA Official Recipe. Created by Salvatore Calabrese at Skybar, Trader Vic''s London, 2009.', 1)
