-- 104. Spritz Veneziano


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a2c2bd7e-c3a4-41ff-bdef-345a475b83d9', 'spritz', 'd6c5b4a3e2f10987564382910047563829174638', '{"en":"Spritz Veneziano"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","aperitivo","bitter","italian","sparkling"}', '{"en":"Italy''s iconic aperitivo cocktail combining prosecco, bitter liqueur (Aperol or Campari), and soda water. Born in Venice, the Spritz is the quintessential Italian happy hour drink with its brilliant orange color and refreshingly bitter-sweet taste."}'::jsonb, '{"created_year":"1800","origin":{"city":"Venice","bar":"Various cafés","country":"Italy"},"creator":{"name":"Austrian soldiers","profession":"military"},"story":{"en":"The Spritz originated in Venice during the 19th century Habsburg occupation. Austrian soldiers found Italian wines too strong and requested them to be \"spritzed\" (spritzen in German means \"to spray\") with water. This evolved into adding soda water to wine. In the 1920s, the Aperol company popularized the modern version by promoting their bitter liqueur as the perfect addition. The classic Venetian formula is 3-2-1: three parts prosecco, two parts Aperol (or Campari for a more bitter version), and one part soda water. It became globally popular in the 2010s, particularly the Aperol Spritz variant."},"named_after":{"en":"Named after the German word \"spritzen\" (to spray/spritz), referring to the addition of soda water."}}'::jsonb, '{"profile":["bitter","refreshing","sparkling"],"description":{"en":"Light, refreshing, and effervescent with a perfect balance of bitter orange, herbal complexity, and prosecco bubbles. The Aperol version is sweeter and more approachable; Campari makes it more intensely bitter."},"first_impression":{"en":"Bright, fizzy prosecco bubbles followed by bittersweet orange and herbs"},"finish":{"en":"Clean, refreshing finish with lingering pleasant bitterness"},"balance":{"en":"Perfectly balanced between bitter, sweet, and sparkling - the ultimate aperitivo"}}'::jsonb, '{"best_time":["afternoon","early_evening"],"occasions":["aperitivo","casual","brunch","social_gathering"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Perfect with Italian aperitivo snacks: olives, chips, focaccia, prosciutto, cheese, bruschetta. The bitterness stimulates appetite and cuts through rich foods."},"ideal_for":{"en":"Perfect for aperitivo hour (5-7pm) and anyone who enjoys light, refreshing, low-ABV drinks. Great for those new to bitter cocktails - start with Aperol before trying Campari."}}'::jsonb, '[{"ingredient_id":"ING_PROSECCO","quantity":{"amount":90,"unit":"ml"},"display_name":"Prosecco","notes":null},{"ingredient_id":"ING_APEROL","quantity":{"amount":60,"unit":"ml"},"display_name":"Aperol (or Campari)","notes":null},{"ingredient_id":"ING_SODA_WATER","quantity":{"amount":30,"unit":"ml"},"display_name":"Soda water","notes":null}]'::jsonb, 'build', '{"en":"Fill a large wine glass or rocks glass with ice. Pour prosecco, then Aperol (or Campari), then soda water. Stir gently to combine. Garnish with an orange slice."}'::jsonb, 'Wine glass or Rocks glass', '{"en":"Orange slice"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_PROSECCO"}', '{"bitter","refreshing","sparkling"}', 8, 140, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","casual","brunch","social_gathering"}', false, false, '{"aperol-spritz","campari-spritz","select-spritz","hugo-spritz"}', 'Classic ratio is 3-2-1 (prosecco-Aperol-soda). Aperol = sweeter and more approachable. Campari = more bitter and traditional. Always serve in a large glass filled with ice. Don''t overstir - keep it bubbly.', 'low', 98, 'https://iba-world.com/spritz/', 'IBA Official Recipe. Historical information from Venetian aperitivo culture and Aperol company archives.', 1)
