-- 105. Spritz Veneziano


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('4372e7f1-9979-468f-a5a7-93d47e027d6b', 'spritz-veneziano', 'b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4f5a6b7c8', '{"en":"Spritz Veneziano"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","italian","aperitivo","prosecco","bitter","iconic"}', '{"en":"The iconic Italian aperitivo cocktail combining Prosecco, Aperol, and soda water. This refreshing, low-ABV drink with its distinctive sunset-orange color has become a global phenomenon, embodying the Italian art of aperitivo hour and la dolce vita lifestyle."}'::jsonb, '{"created_year":"1919","origin":{"city":"Venice","bar":"Unknown","country":"Italy"},"creator":{"name":"Unknown","profession":"unknown"},"story":{"en":"The Spritz originated in Venice during the 19th century when the Austro-Hungarian Empire controlled the region. Austrian soldiers found Italian wine too strong and requested it be \"spritzed\" (sprayed) with water. The modern Aperol Spritz emerged after Aperol''s creation in 1919 in Padua. While Spritzes were popular locally for decades, the Aperol Spritz exploded globally in the 2000s-2010s, becoming one of the world''s most recognized cocktails. Its 3-2-1 ratio (3 parts Prosecco, 2 parts Aperol, 1 splash soda) became the standard serve."},"named_after":{"en":"\"Spritz\" comes from the German word \"spritzen\" (to spray/spritz), referencing the original practice of diluting wine with water."}}'::jsonb, '{"profile":["bitter","bubbly","refreshing","light"],"description":{"en":"Light and refreshing with Aperol''s signature bitter-orange flavor, Prosecco''s fruity effervescence, and a gentle splash of soda. The bitterness is mild and approachable, balanced by sweet citrus notes and sparkling wine brightness. Incredibly sessionable and refreshing."},"first_impression":{"en":"Sparkling effervescence with gentle bitter-orange and citrus sweetness"},"finish":{"en":"Clean, refreshing finish with subtle bitter notes and citrus brightness"},"balance":{"en":"Perfectly balanced between bitter, sweet, and bubbly - the quintessential aperitivo"}}'::jsonb, '{"best_time":["afternoon","early_evening","aperitif"],"occasions":["aperitivo","social_gathering","casual","outdoor_dining","summer_party"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Perfect with Italian cicchetti, olives, salted nuts, prosciutto, bruschetta, light seafood, and cheese plates."},"ideal_for":{"en":"Perfect for aperitivo hour and social drinking. Ideal for those seeking a light, refreshing, low-ABV option. Great for introducing people to Italian aperitivo culture and outdoor cafe life."}}'::jsonb, '[{"ingredient_id":"ING_PROSECCO","quantity":{"amount":90,"unit":"ml"},"display_name":"Prosecco","notes":null},{"ingredient_id":"ING_APEROL","quantity":{"amount":60,"unit":"ml"},"display_name":"Aperol","notes":null},{"ingredient_id":"ING_SODA_WATER","quantity":{"amount":30,"unit":"ml"},"display_name":"Soda water (splash)","notes":null}]'::jsonb, 'build', '{"en":"Fill a large wine glass or rocks glass with ice cubes. Add Aperol, then Prosecco. Top with a splash of soda water. Stir gently once to combine. Garnish with an orange slice."}'::jsonb, 'Large wine glass (or Rocks glass)', '{"en":"Orange slice (half-wheel)"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_PROSECCO"}', '{"bitter","bubbly","refreshing","light"}', 8, 140, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free","low-abv"}', '{"spring","summer","autumn"}', '{"aperitivo","casual","social","outdoor"}', false, false, '{"campari-spritz","select-spritz","limoncello-spritz"}', 'Classic ratio is 3-2-1 (Prosecco-Aperol-Soda). Use large wine glass filled with ice. Aperol is essential - Campari makes a different drink (Negroni Sbagliato). Garnish with orange slice, not twist.', 'mid', 100, 'https://iba-world.com/spritz/', 'IBA Official Recipe. Originated in Venice, popularized globally in 2000s-2010s.', 1)
