-- 106. Suffering Bastard


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('49eeb82a-b3a9-4e13-baef-0689139e5c2e', 'suffering-bastard', 'e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4', '{"en":"Suffering Bastard"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","gin","brandy","tiki","refreshing","hangover"}', '{"en":"A legendary hangover cure from Cairo combining gin and brandy with lime, ginger beer, and Angostura bitters. Refreshing, spicy, and supposedly medicinal - perfect for \"suffering bastards.\""}'::jsonb, '{"created_year":"1942","origin":{"city":"Cairo","bar":"Shepheard''s Hotel","country":"Egypt"},"creator":{"name":"Joe Scialom","profession":"bartender"},"story":{"en":"Created by Joe Scialom at Shepheard''s Hotel in Cairo during World War II. The drink was allegedly invented as a hangover cure for British officers stationed in North Africa. The name refers to the hungover soldiers who would stumble into the bar seeking relief. The drink became so popular that Scialom reportedly made \"by the barrel\" during the war years."},"named_after":{"en":"Named after the hungover British officers who were \"suffering bastards\" in need of a cure."}}'::jsonb, '{"profile":["spicy","refreshing","complex"],"description":{"en":"Complex blend of gin and brandy with bright lime, spicy ginger beer, and aromatic bitters. Refreshing, warming, and surprisingly balanced despite the multiple spirits."},"first_impression":{"en":"Bright citrus and spicy ginger with herbal gin notes"},"finish":{"en":"Long, warming finish with ginger spice and aromatic bitters"},"balance":{"en":"Well-balanced between spirit strength, citrus brightness, and ginger spice"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","recovery","party","casual"],"seasons":["summer","spring"],"food_pairings":{"en":"Excellent with spicy foods, Middle Eastern cuisine, grilled meats, or as a brunch refresher."},"ideal_for":{"en":"Perfect for those seeking a refreshing, complex drink or a legendary hangover cure. Ideal for tiki enthusiasts and history buffs."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_BRANDY","quantity":{"amount":30,"unit":"ml"},"display_name":"Brandy","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GINGER_BEER","quantity":{"amount":120,"unit":"ml"},"display_name":"Ginger beer","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":2,"unit":"dashes"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'build', '{"en":"Pour gin, brandy, and lime juice into a highball glass filled with ice. Top with ginger beer. Add two dashes of Angostura bitters. Stir gently."}'::jsonb, 'Highball glass', '{"en":"Mint sprig and lime wheel"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_GIN","ING_BRANDY"}', '{"spicy","refreshing","complex"}', 10, 180, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring"}', '{"brunch","party","casual"}', false, false, '{"original-suffering-bastard"}', 'Use quality ginger beer for best results. The combination of gin and brandy is traditional - don''t substitute. Originally created as a hangover cure.', 'mid', 55, 'https://iba-world.com/suffering-bastard/', 'IBA Official Recipe', 1)
