-- 107. Three Dots and a Dash


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('87024b08-eaa1-4ffe-a31f-fa72a312f565', 'three-dots-and-a-dash', 'e4f5a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3', '{"en":"Three Dots and a Dash"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","tiki","rum","tropical","complex"}', '{"en":"A complex tiki classic combining multiple rums with tropical fruit flavors and exotic spices. The name refers to Morse code for \"V\" (for Victory), created during World War II. This elaborate cocktail showcases the artistry of tiki culture with its layered flavors and dramatic presentation."}'::jsonb, '{"created_year":"1941","origin":{"city":"Hollywood","bar":"Don the Beachcomber''s","country":"USA"},"creator":{"name":"Donn Beach (Ernest Raymond Beaumont Gantt)","profession":"bartender"},"story":{"en":"Created by Donn Beach (Don the Beachcomber) in 1941 during World War II. The cocktail''s name comes from Morse code: three dots and a dash represents the letter \"V\" for Victory. The garnish of three cherries and a pineapple chunk on a skewer literally illustrates the Morse code pattern. It became one of the most celebrated tiki drinks, exemplifying the complex, multi-rum style that defined the era."},"named_after":{"en":"Named after Morse code for the letter \"V\" (Victory) - three dots and a dash (···-), a symbol of Allied victory in World War II."}}'::jsonb, '{"profile":["tropical","fruity","complex","spiced"],"description":{"en":"Complex and layered with tropical fruit flavors, warm spice notes, and a balanced rum backbone. The combination of multiple rums creates depth while citrus and allspice add brightness and exotic character."},"first_impression":{"en":"Tropical fruit sweetness with honey and citrus brightness"},"finish":{"en":"Long, warm finish with lingering spice and rum character"},"balance":{"en":"Perfectly balanced between sweet, sour, and spirit strength with complex spice undertones"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["celebration","tiki_party","summer_gathering","special_occasion"],"seasons":["spring","summer"],"food_pairings":{"en":"Pairs wonderfully with Polynesian cuisine, grilled pineapple, coconut shrimp, teriyaki chicken, and tropical fruit platters."},"ideal_for":{"en":"Perfect for tiki enthusiasts and rum lovers who appreciate complex, multi-layered cocktails. Ideal for those seeking an authentic piece of cocktail history with dramatic presentation."}}'::jsonb, '[{"ingredient_id":"ING_RUM_AGED","quantity":{"amount":45,"unit":"ml"},"display_name":"Aged rum","notes":null},{"ingredient_id":"ING_RUM_DEMERARA","quantity":{"amount":15,"unit":"ml"},"display_name":"Demerara rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Honey syrup","notes":null},{"ingredient_id":"ING_FALERNUM","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Falernum","notes":null},{"ingredient_id":"ING_ALLSPICE_DRAM","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Allspice dram (Pimento dram)","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with crushed ice. Shake vigorously until well-chilled. Pour unstrained (with the ice) into a tiki mug or tall glass. Garnish elaborately with three cherries and a pineapple chunk on a cocktail pick, representing the Morse code pattern."}'::jsonb, 'Tiki mug or tall glass', '{"en":"Three maraschino cherries and pineapple chunk on a pick (representing Morse code ···-)"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_AGED","ING_RUM_DEMERARA"}', '{"tropical","fruity","complex","spiced"}', 15, 240, 'hard', 180, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"celebration","party","special_occasion"}', false, false, '{"modern-tiki-variations"}', 'Multiple rum blend is essential for authenticity. Honey syrup is 1:1 honey to water. Falernum and allspice dram are specialty tiki ingredients - keep in stock. Garnish is important for visual storytelling.', 'premium', 70, 'https://iba-world.com/three-dots-and-a-dash/', 'IBA Official Recipe. Historical information from Beachbum Berry''s tiki cocktail research.', 1)
