-- 108. Tipperary


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('3ae218f3-6021-49b8-a0fc-5676c7185bfa', 'tipperary', 'c3d4e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2', '{"en":"Tipperary"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","whiskey","herbal","classic","irish"}', '{"en":"An elegant Irish whiskey cocktail that combines the smoothness of Irish whiskey with the herbal complexity of sweet vermouth and the distinctive flavor of green Chartreuse."}'::jsonb, '{"created_year":"1916","origin":{"city":"New York City","bar":"Unknown","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Tipperary cocktail first appeared in Hugo Ensslin''s 1916 \"Recipes for Mixed Drinks.\" Named after the Irish county and the popular WWI song \"It''s a Long Way to Tipperary,\" this cocktail celebrates Irish whiskey with a sophisticated blend of vermouth and Chartreuse. It gained renewed popularity during the craft cocktail renaissance of the 2000s."},"named_after":{"en":"Named after County Tipperary in Ireland and the WWI song \"It''s a Long Way to Tipperary.\""}}'::jsonb, '{"profile":["herbal","complex","smooth"],"description":{"en":"Smooth Irish whiskey with herbal, honeyed notes from the Chartreuse and sweet vermouth. Complex and well-balanced with a warming finish."},"first_impression":{"en":"Herbal and honeyed with Irish whiskey warmth"},"finish":{"en":"Long, warming finish with lingering herbal complexity"},"balance":{"en":"Well-balanced between whiskey smoothness, vermouth sweetness, and Chartreuse herbal notes"}}'::jsonb, '{"best_time":["evening","afternoon"],"occasions":["aperitivo","celebration","date_night","st_patricks_day"],"seasons":["autumn","winter","spring"],"food_pairings":{"en":"Excellent with Irish stew, shepherd''s pie, aged cheddar, or as a pre-dinner aperitif."},"ideal_for":{"en":"Perfect for Irish whiskey enthusiasts and those who appreciate herbal, complex cocktails. Ideal for St. Patrick''s Day or anyone exploring classic cocktails."}}'::jsonb, '[{"ingredient_id":"ING_WHISKEY_IRISH","quantity":{"amount":50,"unit":"ml"},"display_name":"Irish whiskey","notes":null},{"ingredient_id":"ING_VERMOUTH_SWEET","quantity":{"amount":25,"unit":"ml"},"display_name":"Sweet vermouth","notes":null},{"ingredient_id":"ING_CHARTREUSE_GREEN","quantity":{"amount":25,"unit":"ml"},"display_name":"Green Chartreuse","notes":null}]'::jsonb, 'stir', '{"en":"Pour all ingredients into a mixing glass filled with ice. Stir well until chilled. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_WHISKEY_IRISH"}', '{"herbal","complex","smooth"}', 30, 180, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring"}', '{"aperitivo","celebration","date_night"}', false, false, '{"tipperary-no-2"}', 'Stir, don''t shake, to maintain clarity. The green Chartreuse provides a distinctive herbal character. Some variations use equal parts of all three ingredients.', 'mid', 60, 'https://iba-world.com/tipperary/', 'IBA Official Recipe', 1)
