-- 109. Tommy's Margarita


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f65d9035-90a5-41f3-a9e0-af9ffd3569cd', 'tommys-margarita', 'f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1', '{"en":"Tommy''s Margarita"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","tequila","agave","citrus","modern-classic"}', '{"en":"A refined variation of the classic Margarita that replaces triple sec with agave nectar. This simpler, purer version showcases the tequila''s flavor and has become a modern classic."}'::jsonb, '{"created_year":"1990","origin":{"city":"San Francisco","bar":"Tommy''s Mexican Restaurant","country":"USA"},"creator":{"name":"Julio Bermejo","profession":"bartender"},"story":{"en":"Created in 1990 by Julio Bermejo at his family''s Tommy''s Mexican Restaurant in San Francisco. Bermejo, a tequila advocate, wanted to create a Margarita that better showcased 100% agave tequila. By replacing orange liqueur with agave nectar, he created a cleaner, more tequila-forward version that gained worldwide recognition. The drink helped launch the premium tequila movement and established Tommy''s as a tequila destination."},"named_after":{"en":"Named after Tommy''s Mexican Restaurant in San Francisco where it was created."}}'::jsonb, '{"profile":["agave","citrus","clean"],"description":{"en":"Pure agave sweetness from tequila and nectar, bright lime tartness, and clean finish. More tequila-forward than traditional Margarita with no orange notes."},"first_impression":{"en":"Clean agave and bright lime with tequila character"},"finish":{"en":"Long, clean finish with lingering agave sweetness"},"balance":{"en":"Perfectly balanced between tequila strength, agave sweetness, and lime tartness"}}'::jsonb, '{"best_time":["evening","afternoon"],"occasions":["casual","party","mexican_dinner","taco_night"],"seasons":["all_seasons"],"food_pairings":{"en":"Excellent with Mexican cuisine, tacos, ceviche, guacamole, or grilled meats."},"ideal_for":{"en":"Perfect for tequila purists and those who want to taste quality agave spirits. Ideal for anyone seeking a cleaner, more authentic Margarita."}}'::jsonb, '[{"ingredient_id":"ING_TEQUILA_BLANCO","quantity":{"amount":60,"unit":"ml"},"display_name":"Blanco tequila","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_AGAVE_NECTAR","quantity":{"amount":15,"unit":"ml"},"display_name":"Agave nectar","notes":null}]'::jsonb, 'shake', '{"en":"Shake all ingredients with ice. Strain into a rocks glass filled with ice. Optionally rim with salt."}'::jsonb, 'Rocks glass', '{"en":"Lime wheel (optional salt rim)"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_TEQUILA_BLANCO"}', '{"agave","citrus","clean"}', 20, 160, 'easy', 45, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-season"}', '{"casual","party","mexican_dinner"}', false, false, '{}', 'Use 100% agave tequila for best results. Agave nectar is essential - don''t substitute with simple syrup. Salt rim is optional but traditional.', 'mid', 85, 'https://iba-world.com/tommys-margarita/', 'IBA Official Recipe', 1)
