-- 110. Trinidad Sour


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b0999727-6026-42c4-a941-66ed98b0326c', 'trinidad-sour', 'f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0', '{"en":"Trinidad Sour"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","modern","sour","bitter","revolutionary","bold"}', '{"en":"A revolutionary modern cocktail that flips the traditional formula by using Angostura bitters as the base spirit instead of an accent. This bold creation showcases how bitters can be the star, balanced with rye whiskey, orgeat, and lemon. The result is an intensely aromatic and complex drink."}'::jsonb, '{"created_year":"2009","origin":{"city":"New York City","bar":"Clover Club","country":"USA"},"creator":{"name":"Giuseppe Gonzalez","profession":"bartender"},"story":{"en":"Created by Giuseppe Gonzalez at Clover Club in Brooklyn in 2009, the Trinidad Sour was a groundbreaking cocktail that challenged conventional thinking. Gonzalez wanted to showcase Angostura bitters - traditionally used by the dash - as the primary ingredient. The audacious proportions (1.5 oz of bitters!) shouldn''t work on paper, but the careful balance with orgeat''s sweetness, lemon''s acidity, and rye''s spice creates something remarkably drinkable and complex. It inspired bartenders worldwide to rethink ingredient hierarchies."},"named_after":{"en":"Named after Trinidad, the birthplace of Angostura bitters (House of Angostura was founded in Trinidad in 1824)."}}'::jsonb, '{"profile":["bitter","spicy","aromatic","complex"],"description":{"en":"Intensely aromatic with powerful baking spices, clove, cinnamon, and gentian bitterness balanced by almond-sweet orgeat and bright lemon acidity. The rye adds a spicy backbone that ties everything together. Surprisingly smooth despite the bold bitters content."},"first_impression":{"en":"Powerful aromatic spices and bitterness with almond sweetness"},"finish":{"en":"Long, warming finish with lingering spice and bitter complexity"},"balance":{"en":"Remarkably balanced given the extreme proportions - bitter, sweet, sour, and spicy in harmony"}}'::jsonb, '{"best_time":["evening","after_dinner"],"occasions":["digestivo","cocktail_party","date_night","tasting_menu"],"seasons":["autumn","winter"],"food_pairings":{"en":"Pairs well with rich meats, dark chocolate, spiced desserts, aged cheeses, and bold Caribbean cuisine."},"ideal_for":{"en":"Perfect for adventurous cocktail enthusiasts who appreciate bitter flavors and innovative mixology. Ideal for amaro and bitters lovers. Great as a conversation starter and digestif."}}'::jsonb, '[{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":45,"unit":"ml"},"display_name":"Angostura bitters","notes":null},{"ingredient_id":"ING_RYE_WHISKEY","quantity":{"amount":15,"unit":"ml"},"display_name":"Rye whiskey","notes":null},{"ingredient_id":"ING_ORGEAT","quantity":{"amount":30,"unit":"ml"},"display_name":"Orgeat syrup","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled (15-20 seconds is crucial for proper dilution). Double strain into a chilled coupe or Nick & Nora glass. Garnish with a lemon twist, expressing the oils over the drink."}'::jsonb, 'Coupe or Nick & Nora glass', '{"en":"Lemon twist (expressed)"}'::jsonb, 'none', 'up', '{"ING_ANGOSTURA_BITTERS"}', '{"bitter","spicy","aromatic","complex"}', 20, 180, 'easy', 90, '{"tree_nuts","sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free"}', 3, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"digestivo","date_night","cocktail_party"}', false, false, '{"bourbon-trinidad-sour","trinidad-especial"}', 'Yes, 1.5 oz of Angostura is correct! Proper dilution through shaking is critical. Quality orgeat is essential. Express lemon oils generously. This drink converts bitter skeptics.', 'mid', 70, 'https://iba-world.com/trinidad-sour/', 'IBA Official Recipe. Created by Giuseppe Gonzalez at Clover Club, Brooklyn, 2009.', 1)
