VALUES ('23bc3ed4-62c5-4c96-a060-b85b89f04f0f', 'yellow-bird', 'a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6', '{"en":"Yellow Bird"}'::jsonb, 'iba_official', 'NewEraDrinks', '{"iba","official","rum","tropical","caribbean","fruity"}', '{"en":"A vibrant Caribbean cocktail combining rum with Galliano, triple sec, and fresh lime juice. This tropical drink features a beautiful golden color and a perfect balance of sweet and citrus flavors."}'::jsonb, '{"created_year":"1960","origin":{"city":"Kingston","bar":"Unknown","country":"Jamaica"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Yellow Bird was created in Jamaica in the 1960s and became popular in Caribbean resorts. Named after the Haitian folk song \"Yellow Bird\" (Choucoune), which was popularized by various artists in the 1950s-60s. The drink''s golden color comes from Galliano and represents the titular yellow bird. It became a staple at tiki bars and tropical destinations worldwide."},"named_after":{"en":"Named after the Haitian folk song \"Yellow Bird\" (Choucoune), popular in the 1950s-60s."}}'::jsonb, '{"profile":["tropical","citrus","sweet"],"description":{"en":"Bright tropical flavors with rum sweetness, herbal vanilla notes from Galliano, orange from triple sec, and fresh lime tartness. Smooth and refreshing with a complex finish."},"first_impression":{"en":"Tropical sweetness with bright citrus and vanilla notes"},"finish":{"en":"Smooth, lingering finish with herbal and citrus complexity"},"balance":{"en":"Well-balanced between rum richness, liqueur sweetness, and lime tartness"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","party","celebration","tiki_night"],"seasons":["summer","spring"],"food_pairings":{"en":"Excellent with Caribbean cuisine, jerk chicken, tropical fruits, or coconut-based desserts."},"ideal_for":{"en":"Perfect for tiki enthusiasts and those who love tropical, fruity cocktails. Ideal for beach parties or anyone seeking a taste of the Caribbean."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_GALLIANO","quantity":{"amount":15,"unit":"ml"},"display_name":"Galliano","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":15,"unit":"ml"},"display_name":"Triple sec","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake well. Strain into a chilled cocktail glass."}'::jsonb, 'Cocktail glass (Martini)', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_RUM_WHITE"}', '{"tropical","citrus","sweet"}', 22, 170, 'easy', 45, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring"}', '{"beach","party","celebration"}', false, false, '{"yellow-bird-frozen"}', 'The golden color should be vibrant. Can be served frozen for a different texture. Some variations add pineapple juice.', 'mid', 65, 'https://iba-world.com/yellow-bird/', 'IBA Official Recipe', 1)
