-- 117. B-52


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b4124495-7df0-4524-9c7b-c2ca7588acc1', 'b-52', 'b52_shot_famous_layered_bomber', '{"en":"B-52"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","layered","coffee","creamy"}', '{"en":"A visually stunning layered shot featuring coffee liqueur, Irish cream, and Grand Marnier. Named after the B-52 Stratofortress bomber, this classic shooter is known for its distinct three-layer appearance and smooth, sweet taste."}'::jsonb, '{"created_year":"1977","origin":{"city":"Alberta","bar":"Banff Springs Hotel","country":"Canada"},"creator":{"name":"Peter Fich","profession":"bartender"},"story":{"en":"Created by bartender Peter Fich at the Banff Springs Hotel in Alberta, Canada in 1977. The shot was named after the B-52 Stratofortress bomber aircraft. The layering technique creates a visual effect reminiscent of the aircraft''s stratified flight levels."},"named_after":{"en":"Named after the B-52 Stratofortress, an American long-range strategic bomber aircraft."}}'::jsonb, '{"profile":["sweet","creamy","coffee"],"description":{"en":"Smooth and sweet with distinct layers of coffee, cream, and orange flavors. The Kahlúa provides rich coffee notes, Baileys adds creamy sweetness, and Grand Marnier brings a citrus finish."},"first_impression":{"en":"Sweet coffee and cream flavors with a hint of orange"},"finish":{"en":"Smooth finish with lingering orange liqueur warmth"},"balance":{"en":"Well-balanced layers that blend smoothly when consumed"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","nightlife"],"seasons":["all_seasons"],"food_pairings":{"en":"Best enjoyed on its own as a party shot. Can be paired with chocolate desserts or coffee-based sweets."},"ideal_for":{"en":"Perfect for those who enjoy sweet, visually impressive shots. A crowd-pleaser at parties and great for coffee liqueur lovers."}}'::jsonb, '[{"ingredient_id":"ING_KAHLUA","quantity":{"amount":15,"unit":"ml"},"display_name":"Kahlúa","notes":null},{"ingredient_id":"ING_BAILEYS","quantity":{"amount":15,"unit":"ml"},"display_name":"Baileys Irish Cream","notes":null},{"ingredient_id":"ING_GRAND_MARNIER","quantity":{"amount":15,"unit":"ml"},"display_name":"Grand Marnier","notes":null}]'::jsonb, 'layer', '{"en":"Layer the ingredients in a shot glass in the following order: Kahlúa (bottom), Baileys Irish Cream (middle), Grand Marnier (top). Pour each layer slowly over the back of a bar spoon to maintain separation."}'::jsonb, 'Shot glass', '{"en":"None (the layered appearance is the visual appeal)"}'::jsonb, 'none', 'shot', '{"ING_KAHLUA","ING_BAILEYS","ING_GRAND_MARNIER"}', '{"sweet","creamy","coffee"}', 25, 160, 'medium', 90, '{"milk","sulphites"}', '{"lactose","alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"party","celebration","nightlife"}', false, false, '{"b-53","b-54","flaming-b-52"}', 'Layer carefully using the back of a bar spoon. Pour slowly to maintain distinct layers. Can be served flaming (ignite Grand Marnier) if requested - ensure safety precautions.', 'mid', 85, 'https://en.wikipedia.org/wiki/B-52_(cocktail)', 'Classic layered shot recipe. Created by Peter Fich in 1977.', 1)
