-- 119. Bananas Foster


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('02ded069-6156-4e8f-89df-686993e13b83', 'bananas-foster', 'bananas_foster_rum_banana_liqueur_cream', '{"en":"Bananas Foster"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","sweet","tropical","rum-based"}', '{"en":"A decadent cocktail inspired by the famous New Orleans dessert, combining dark rum, banana liqueur, and cream to recreate the flavors of caramelized bananas, butter, and brown sugar in liquid form."}'::jsonb, '{"created_year":"2000s","origin":{"country":"USA"},"story":{"en":"The Bananas Foster cocktail is a modern creation inspired by the classic dessert that was created in 1951 at Brennan''s Restaurant in New Orleans. The original dessert, made with bananas, butter, brown sugar, cinnamon, dark rum, and banana liqueur, flambéed tableside, became an iconic New Orleans specialty. Bartenders in the 2000s began creating cocktail versions to capture those beloved flavors in a drinkable form. This liquid tribute combines the key flavors of rum, banana, and cream to evoke the rich, caramelized dessert."},"named_after":{"en":"Named after the classic New Orleans dessert \"Bananas Foster,\" which was created at Brennan''s Restaurant and named after Richard Foster, a local civic leader."}}'::jsonb, '{"profile":["creamy","sweet","fruity","caramel"],"description":{"en":"Sweet and tropical with prominent banana flavor balanced by rich rum and creamy texture. The dark rum provides warmth and depth reminiscent of the caramelized sugar in the original dessert, while the banana liqueur delivers authentic banana sweetness."},"first_impression":{"en":"Sweet banana cream with immediate rum warmth"},"finish":{"en":"Lingering banana sweetness with warm rum and caramel notes"},"balance":{"en":"Sweet and dessert-forward, with rum providing backbone to the banana and cream"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["dessert","celebration","tropical_party","mardi_gras"],"seasons":["all_seasons"],"food_pairings":{"en":"Perfect as a dessert replacement or with banana-based desserts, bread pudding, or vanilla ice cream. Also pairs well with caramel desserts and pecan pie."},"ideal_for":{"en":"Perfect for lovers of the classic New Orleans dessert and tropical flavors. Ideal for Mardi Gras celebrations, those who enjoy banana-flavored drinks, and anyone seeking a sweet, indulgent cocktail with Southern charm."}}'::jsonb, '[{"ingredient_id":"ING_DARK_RUM","quantity":{"amount":45,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_BANANA_LIQUEUR","quantity":{"amount":30,"unit":"ml"},"display_name":"Banana liqueur (Crème de banane)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 10-15 seconds until well-chilled and frothy. Strain into a chilled coupe or martini glass. Optionally, garnish with caramelized banana slice and a sprinkle of cinnamon."}'::jsonb, 'Coupe (or Martini glass)', '{"en":"Caramelized banana slice, cinnamon dusting, or banana chip"}'::jsonb, 'none', 'up', '{"ING_DARK_RUM"}', '{"creamy","sweet","fruity","caramel"}', 18, 310, 'easy', 60, '{"milk"}', '{"lactose","alcohol"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"dessert","celebration"}', false, false, '{"frozen-bananas-foster","bananas-foster-colada","flaming-bananas-foster"}', 'Inspired by the classic New Orleans dessert. Some versions add a dash of cinnamon syrup or butterscotch liqueur for extra authenticity. Can be served frozen for a more dessert-like experience. For dramatic presentation, can lightly flame the banana garnish tableside (use caution).', 'mid', 55, 'https://www.diffordsguide.com/cocktails/recipe/8713/bananas-foster-cocktail', 'Cocktail version of the famous 1951 New Orleans dessert.', 1)
