-- 124. Blue Hawaiian


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a6d6e70a-b890-468a-8fd3-6576354e6a60', 'blue-hawaiian', 'blue_hawaiian_famous_tiki_tropical_cocktail', '{"en":"Blue Hawaiian"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","creamy","sweet","blue"}', '{"en":"The Blue Hawaiian is a vibrant tropical cocktail featuring rum, blue curaçao, pineapple juice, and cream of coconut. Its striking blue color evokes Hawaiian waters, making it a visually stunning and deliciously tropical vacation drink."}'::jsonb, '{"created_year":"1957","origin":{"city":"Honolulu","bar":"Hilton Hawaiian Village","country":"USA"},"creator":{"name":"Harry Yee","profession":"bartender"},"story":{"en":"The Blue Hawaiian was created in 1957 by legendary bartender Harry Yee at the Hilton Hawaiian Village in Waikiki. Yee invented the drink as a showcase for Bols Blue Curaçao, which had just been introduced to Hawaii. The stunning blue color was meant to evoke the crystal-clear Hawaiian waters. Harry Yee is also credited with being the first bartender to garnish drinks with orchids and paper umbrellas, innovations that became synonymous with tiki culture. The Blue Hawaiian became an instant classic and remains one of Hawaii''s signature cocktails."},"named_after":{"en":"Named for the vibrant blue color reminiscent of Hawaiian ocean waters and tropical paradise."}}'::jsonb, '{"profile":["tropical","creamy","sweet"],"description":{"en":"Tropical and refreshing with a perfect balance of citrus and coconut. The Blue Hawaiian combines bright orange notes from blue curaçao with pineapple sweetness and coconut creaminess, all lifted by rum. Smooth, creamy, and undeniably tropical."},"first_impression":{"en":"Sweet tropical fruits and orange citrus hit first, followed by creamy coconut waves"},"finish":{"en":"Smooth, creamy finish with lingering coconut and tropical fruit notes"},"balance":{"en":"Well-balanced between sweet tropical flavors and creamy texture, with rum providing subtle backbone"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","luau","vacation"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Hawaiian poke, kalua pork, grilled fish, coconut shrimp, or tropical fruit platters. Also pairs well with teriyaki dishes and island-style BBQ."},"ideal_for":{"en":"Perfect for tropical vacation seekers and Instagram-worthy drink enthusiasts. Ideal for anyone who wants a visually stunning cocktail that tastes as good as it looks. Great for pool parties and beachside celebrations."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":45,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_BLUE_CURACAO","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Blue curaçao","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":90,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_COCONUT_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Cream of coconut","notes":null}]'::jsonb, 'blend', '{"en":"Add all ingredients to a blender with 1 cup of crushed ice. Blend until smooth and creamy. Pour into a hurricane glass. Garnish with pineapple wedge and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge, maraschino cherry, orchid flower"}'::jsonb, 'blended', 'frozen', '{"ING_RUM_WHITE"}', '{"tropical","creamy","sweet"}', 10, 300, 'easy', 120, '{"coconut","sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","pool_party","luau","vacation"}', false, false, '{"blue-hawaii","swimming-pool"}', 'Created by Harry Yee, pioneer of tiki garnishes. Can be shaken instead of blended for a non-frozen version. The vibrant blue color is essential - use quality blue curaçao. Garnish with orchid if available for authentic Hawaiian presentation.', 'mid', 88, 'https://www.diffordsguide.com/cocktails/recipe/506/blue-hawaiian', 'Created by Harry Yee at Hilton Hawaiian Village, 1957. Historical information from tiki cocktail archives.', 1)
