-- 127. Bronx


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('7b4d0294-c3cc-4970-b6a5-08b43b29280b', 'bronx', 'k1l2m3n4o5p6q7r8s9t0u1v2w3x4y5z6a7b8c9d0', '{"en":"Bronx"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","gin","citrus","vermouth","pre-prohibition"}', '{"en":"A perfect martini meets orange juice in this pre-Prohibition classic. The Bronx combines gin with both sweet and dry vermouth plus fresh orange juice, creating a sophisticated yet approachable cocktail. Once America''s third most popular cocktail, it deserves a comeback."}'::jsonb, '{"created_year":"1906","origin":{"city":"Philadelphia","bar":"Waldorf-Astoria Hotel","country":"USA"},"creator":{"name":"Johnnie Solon","profession":"bartender"},"story":{"en":"The Bronx was created around 1906 by bartender Johnnie Solon at the Waldorf-Astoria Hotel''s bar (then located in Philadelphia). According to Solon, a customer asked him to create something new, and inspiration struck after a recent visit to the Bronx Zoo. The cocktail combines gin, sweet and dry vermouth, and orange juice - essentially a Perfect Martini with orange juice added. It became wildly popular in the 1910s and 1920s, rivaling the Martini and Manhattan in popularity. Albert Stevens Crockett wrote in \"Old Waldorf Bar Days\" (1931) that \"the Bronx was to the cocktail world what the zipper has been to the clothing industry.\" However, after Prohibition, the drink faded from prominence as vodka-based cocktails rose to dominance. Modern bartenders are rediscovering its elegant balance."},"named_after":{"en":"Named after the Bronx Zoo in New York, which bartender Johnnie Solon had recently visited when he created the drink."}}'::jsonb, '{"profile":["citrus","herbal","balanced"],"description":{"en":"Bright, citrusy, and beautifully complex. The Bronx balances gin''s botanicals with the herbal complexity of both vermouths, while fresh orange juice adds fruity brightness without overwhelming. It''s like a sophisticated orange creamsicle for grown-ups."},"first_impression":{"en":"Fresh orange juice brightness followed by gin botanicals and herbal vermouth"},"finish":{"en":"Medium finish with lingering orange, juniper, and subtle vermouth complexity"},"balance":{"en":"Expertly balanced between spirit, citrus, and vermouth - each element distinct yet harmonious"}}'::jsonb, '{"best_time":["brunch","afternoon","early_evening"],"occasions":["brunch","aperitivo","social","sophisticated"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Perfect with brunch classics: eggs Benedict, smoked salmon, French toast, and fruit salads. Also pairs well with light seafood, salads, and Mediterranean cuisine."},"ideal_for":{"en":"Perfect for gin enthusiasts who want something more approachable than a martini. Ideal for brunch gatherings and those exploring pre-Prohibition cocktail history. A great choice for orange juice lovers seeking sophistication."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":45,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_SWEET_VERMOUTH","quantity":{"amount":15,"unit":"ml"},"display_name":"Sweet vermouth","notes":null},{"ingredient_id":"ING_DRY_VERMOUTH","quantity":{"amount":15,"unit":"ml"},"display_name":"Dry vermouth","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh orange juice","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled cocktail glass. Garnish with an orange twist."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"citrus","herbal","balanced"}', 18, 155, 'easy', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","nut_free","dairy_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"spring","summer","autumn"}', '{"brunch","aperitivo","social","sophisticated"}', false, false, '{"income-tax","satan-whiskers","golden-bronx"}', 'Must use fresh orange juice - never bottled or concentrate. The classic ratio is 3:1:1:1.5 (gin:sweet vermouth:dry vermouth:orange juice). Use quality vermouths and keep them fresh. Shake hard to properly integrate the orange juice.', 'mid', 50, 'https://www.diffordsguide.com/cocktails/recipe/358/bronx', 'Classic recipe from Waldorf-Astoria (1906), documented in various early 20th century cocktail books.', 1)
