-- 130. Cable Car


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('57f3fc9d-700f-4070-9f58-58ac71722e24', 'cable-car', '4a5b6c7d8e9f0a1b2c3d4e5f6a7b8c9d0e1f2a12', '{"en":"Cable Car"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","rum","citrus","sweet"}', '{"en":"A San Francisco classic featuring spiced rum, orange curaçao, lemon juice, and a cinnamon-sugar rim. The Cable Car is a sophisticated variation on the Sidecar with warming spice notes."}'::jsonb, '{"created_year":"1996","origin":{"city":"San Francisco","bar":"The Starlight Room","country":"USA"},"creator":{"name":"Tony Abou-Ganim","profession":"bartender"},"story":{"en":"Created by legendary bartender Tony Abou-Ganim in 1996 at The Starlight Room atop the Sir Francis Drake Hotel in San Francisco. Abou-Ganim designed the Cable Car as a tribute to San Francisco''s iconic cable car system. He elevated the classic Sidecar by substituting spiced rum for cognac and adding a cinnamon-sugar rim, creating a cocktail that captures the spirit of the city. The drink became immensely popular and is now considered a modern classic, especially in San Francisco."},"named_after":{"en":"Named after San Francisco''s iconic cable car system, a symbol of the city since 1873."}}'::jsonb, '{"profile":["citrus","spicy","sweet"],"description":{"en":"Bright and citrusy with warming spice notes. Spiced rum provides a rich backbone with vanilla and cinnamon, orange curaçao adds sweet orange flavor, lemon juice brings tartness, and the cinnamon-sugar rim enhances the spice character throughout."},"first_impression":{"en":"Sweet cinnamon-sugar rim hits first, followed by citrus and spiced rum warmth"},"finish":{"en":"Long, warming finish with lingering spice and citrus notes"},"balance":{"en":"Well-balanced between sweet, tart, and spicy, with the cinnamon-sugar rim adding extra dimension"}}'::jsonb, '{"best_time":["evening","late_afternoon"],"occasions":["cocktail_hour","date_night","celebration"],"seasons":["autumn","winter"],"food_pairings":{"en":"Excellent with spiced desserts, apple pie, crème brûlée, and roasted nuts. The spice notes complement warm, sweet flavors."},"ideal_for":{"en":"Perfect for rum lovers who enjoy citrus cocktails with a spicy twist. Ideal for those visiting or reminiscing about San Francisco."}}'::jsonb, '[{"ingredient_id":"ING_SPICED_RUM","quantity":{"amount":45,"unit":"ml"},"display_name":"Spiced rum","notes":null},{"ingredient_id":"ING_CURACAO_ORANGE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Orange curaçao","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'shake', '{"en":"Rim a chilled coupe glass with cinnamon-sugar. Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into the prepared glass. Garnish with an orange twist."}'::jsonb, 'Coupe', '{"en":"Cinnamon-sugar rim and orange twist"}'::jsonb, 'none', 'up', '{"ING_SPICED_RUM"}', '{"citrus","spicy","sweet"}', 20, 185, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 2, '{"vegan","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"cocktail_hour","date_night","celebration"}', false, false, '{"sidecar","margarita"}', 'Rim glass with cinnamon-sugar (mix cinnamon and sugar 1:4 ratio). Use quality spiced rum for best results. The cinnamon-sugar rim is essential to the drink''s character - don''t skip it!', 'mid', 77, 'Created by Tony Abou-Ganim at The Starlight Room, 1996', 'Modern classic and San Francisco signature cocktail. Featured in The Modern Mixologist.', 1)
