-- 134. Chocolate Martini


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('71b58e5b-24c7-4c7c-8fc2-db202cf50327', 'chocolate-martini', 'chocolate_martini_vodka_cacao_cream', '{"en":"Chocolate Martini"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","chocolate","sweet","vodka-based","martini-style"}', '{"en":"A decadent dessert cocktail that combines vodka, chocolate liqueur, and cream in a martini glass. This modern classic transforms the chocolate martini into a liquid dessert, perfect for those who want their chocolate fix with a boozy twist."}'::jsonb, '{"created_year":"1990s","origin":{"country":"USA"},"story":{"en":"The Chocolate Martini emerged in the 1990s during the \"martini craze\" when bartenders began putting everything in a martini glass and calling it a martini. Unlike classic martinis, this is purely a dessert cocktail with no vermouth or gin. The drink became enormously popular in upscale bars and restaurants, appealing especially to those who wanted a sophisticated-looking dessert drink. Various versions exist, some using vodka and chocolate liqueur, others adding Irish cream or even using chocolate vodka."},"named_after":{"en":"Named \"Chocolate Martini\" for its chocolate flavor and martini glass presentation, despite having no relation to a traditional martini."}}'::jsonb, '{"profile":["creamy","sweet","chocolate","rich"],"description":{"en":"Rich, velvety chocolate flavor with vodka smoothness and creamy texture. Very sweet and dessert-forward, tasting like liquid chocolate with a boozy kick. The cream softens the chocolate intensity into a silky, indulgent experience."},"first_impression":{"en":"Immediate rich chocolate sweetness with creamy texture"},"finish":{"en":"Long, sweet chocolate finish with lingering cream"},"balance":{"en":"Heavily weighted toward sweetness and chocolate - this is dessert first, cocktail second"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["dessert","date_night","celebration","girls_night"],"seasons":["all_seasons"],"food_pairings":{"en":"Best enjoyed as a dessert replacement. Can accompany chocolate desserts, brownies, or chocolate mousse. Also pairs with strawberries and vanilla ice cream."},"ideal_for":{"en":"Perfect for chocolate lovers who want a sophisticated dessert drink. Ideal for date nights, celebrations, and anyone seeking an indulgent after-dinner cocktail. Popular choice for those who don''t typically enjoy strong spirits."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_CREME_DE_CACAO_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Crème de cacao (dark)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Optionally, rim a chilled martini glass with chocolate syrup and cocoa powder. Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 15 seconds until well-chilled. Double strain into the prepared martini glass. Garnish with chocolate shavings or a chocolate kiss."}'::jsonb, 'Martini glass', '{"en":"Chocolate shavings, chocolate syrup rim, or Hershey''s Kiss"}'::jsonb, 'none', 'up', '{"ING_VODKA"}', '{"creamy","sweet","chocolate","rich"}', 18, 300, 'easy', 90, '{"milk"}', '{"lactose","alcohol"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"dessert","date-night","celebration"}', false, false, '{"espresso-chocolate-martini","white-chocolate-martini","dirty-chocolate-martini"}', 'Not a real martini - it''s a dessert drink. Many variations exist: add Bailey''s for extra cream, espresso for depth, or use chocolate vodka. The chocolate rim is popular but optional. Can use half-and-half instead of cream for lighter version. Very sweet - warn guests accordingly.', 'mid', 80, 'https://www.liquor.com/recipes/chocolate-martini/', 'Popular dessert martini from the 1990s cocktail renaissance.', 1)
