-- 135. Chrysanthemum


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('c0279b4f-2f45-4029-b6d2-52bb8b3a8f09', 'chrysanthemum', 'a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6', '{"en":"Chrysanthemum"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","vermouth","dry","aperitif"}', '{"en":"A sophisticated aperitif cocktail combining dry vermouth, Benedictine, and absinthe. The Chrysanthemum is an elegant, low-alcohol drink with complex herbal notes and a distinctive anise finish."}'::jsonb, '{"created_year":"1930","origin":{"city":"Unknown","bar":"Unknown","country":"Europe"},"creator":{"name":"Hugo Ensslin","profession":"bartender"},"story":{"en":"The Chrysanthemum was created by Hugo Ensslin and published in his 1916 book \"Recipes for Mixed Drinks.\" Named after the flower that symbolizes longevity and rejuvenation in Asian culture, this cocktail represents the pre-Prohibition era''s sophisticated approach to aperitifs. It gained renewed popularity in the 2000s craft cocktail revival."},"named_after":{"en":"Named after the chrysanthemum flower, which symbolizes longevity and rejuvenation in Asian culture, reflecting the drink''s refreshing, aperitif qualities."}}'::jsonb, '{"profile":["dry","herbal","complex"],"description":{"en":"Dry and herbaceous with layers of complexity from Benedictine''s honey-herb notes, vermouth''s botanicals, and absinthe''s distinctive anise character. Light, refreshing, and appetite-stimulating."},"first_impression":{"en":"Dry vermouth botanicals with honeyed Benedictine sweetness and anise hints"},"finish":{"en":"Clean, dry finish with lingering anise and herbal complexity"},"balance":{"en":"Perfectly dry aperitif balance - complex yet light and refreshing"}}'::jsonb, '{"best_time":["afternoon","early_evening"],"occasions":["aperitivo","pre_dinner","garden_party","brunch"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent as an aperitif before meals. Pairs well with light appetizers, olives, almonds, or seafood. Perfect for stimulating the appetite."},"ideal_for":{"en":"Perfect for those who appreciate dry, complex aperitifs. Ideal for adventurous drinkers seeking a sophisticated, low-alcohol cocktail with unique herbal character."}}'::jsonb, '[{"ingredient_id":"ING_DRY_VERMOUTH","quantity":{"amount":60,"unit":"ml"},"display_name":"Dry vermouth","notes":null},{"ingredient_id":"ING_BENEDICTINE","quantity":{"amount":15,"unit":"ml"},"display_name":"Bénédictine","notes":null},{"ingredient_id":"ING_ABSINTHE","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Absinthe","notes":null}]'::jsonb, 'stir', '{"en":"Add all ingredients to a mixing glass filled with ice. Stir gently for 20-30 seconds until well-chilled. Strain into a chilled coupe or Nick & Nora glass. Garnish with an orange twist, expressing the oils over the drink."}'::jsonb, 'Coupe glass', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_DRY_VERMOUTH"}', '{"dry","herbal","complex"}', 18, 120, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","pre_dinner","garden_party","brunch"}', false, false, '{"chrysanthemum-moderne"}', 'Use quality dry vermouth - Dolin Dry recommended. Absinthe can be strong; measure carefully. The drink should be dry and herbal, not sweet. Best served very cold as an aperitif.', 'mid', 50, 'https://www.diffordsguide.com/cocktails/recipe/1188/chrysanthemum', 'Hugo Ensslin''s 1916 "Recipes for Mixed Drinks." Classic pre-Prohibition aperitif.', 1)
