-- 138. Creamsicle


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('31b1d9a1-85e4-48b3-9bdd-b621d9d0f086', 'creamsicle', 'creamsicle_vodka_orange_liqueur_cream_oj', '{"en":"Creamsicle"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","sweet","fruity","vodka-based","orange"}', '{"en":"A nostalgic cocktail inspired by the classic orange-vanilla ice cream bar. Combining vodka, orange liqueur, cream, and orange juice, this drink recreates the beloved childhood treat in liquid form with an adult twist."}'::jsonb, '{"created_year":"1990s","origin":{"country":"USA"},"story":{"en":"The Creamsicle cocktail emerged in the 1990s as bartenders sought to recreate beloved childhood flavors in adult beverages. Inspired by the iconic Creamsicle ice cream bar (orange sherbet with vanilla ice cream center) that was popular from the 1920s onward, mixologists combined orange and cream with vodka to capture that nostalgic orange-vanilla flavor. The drink became especially popular in the 2000s during the dessert cocktail boom, offering a refreshing yet indulgent option that bridges the gap between fruity and creamy cocktails."},"named_after":{"en":"Named after the iconic Creamsicle ice cream bar, known for its orange and vanilla cream combination."}}'::jsonb, '{"profile":["creamy","sweet","fruity","citrus"],"description":{"en":"Sweet and refreshing with pronounced orange citrus notes balanced by smooth vanilla cream. Tastes remarkably like the ice cream bar with a pleasant vodka backbone. The combination of fresh orange juice and cream creates a luscious, dessert-like texture."},"first_impression":{"en":"Sweet orange cream with immediate nostalgia for childhood ice cream bars"},"finish":{"en":"Lingering orange and vanilla sweetness with creamy texture"},"balance":{"en":"Well-balanced between citrus brightness and creamy sweetness - refreshing yet indulgent"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","dessert","pool_party","casual","summer"],"seasons":["spring","summer","all_seasons"],"food_pairings":{"en":"Perfect with citrus desserts, vanilla cake, cheesecake, or as a dessert replacement. Also pairs well with fresh fruit and light pastries."},"ideal_for":{"en":"Perfect for those seeking nostalgic flavors in cocktail form. Ideal for summer gatherings, brunch, poolside drinking, and anyone who loves orange-vanilla combinations. Popular with guests who want a fun, approachable dessert cocktail."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":45,"unit":"ml"},"display_name":"Vodka (vanilla vodka optional)","notes":null},{"ingredient_id":"ING_TRIPLE_SEC","quantity":{"amount":15,"unit":"ml"},"display_name":"Orange liqueur (Triple Sec)","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream (or half-and-half)","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 15 seconds until well-chilled and frothy. Strain into an ice-filled highball or rocks glass. Garnish with an orange wheel or wedge. Can also be served frozen by blending with ice for a slushy texture."}'::jsonb, 'Highball (or Rocks glass)', '{"en":"Orange wheel or wedge, whipped cream (optional)"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_VODKA"}', '{"creamy","sweet","fruity","citrus"}', 12, 240, 'easy', 45, '{"milk"}', '{"lactose","alcohol"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"spring","summer","all-seasons"}', '{"brunch","dessert","casual","pool-party"}', false, false, '{"frozen-creamsicle","creamsicle-martini","orange-dream-cocktail"}', 'Use vanilla vodka for more authentic Creamsicle flavor. Fresh orange juice is essential - never use concentrate. Can add a splash of vanilla extract if not using vanilla vodka. Popular served frozen/blended in summer. Very Instagram-friendly presentation.', 'low', 70, 'https://www.liquor.com/recipes/creamsicle-cocktail/', 'Nostalgic cocktail inspired by the classic orange-vanilla ice cream bar.', 1)
