-- 139. Death in the Afternoon


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('4d201043-c965-458d-bf45-ec9fd8d5323d', 'death-in-the-afternoon', 'd6e5f4c3b2a1908172635445362718192021222324', '{"en":"Death in the Afternoon"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","champagne","literary","absinthe"}', '{"en":"Created by Ernest Hemingway, this deceptively simple cocktail combines absinthe with champagne. Named after Hemingway''s 1932 book about bullfighting, it''s a dangerous, elegant drink that goes down easily but packs a powerful punch. The opalescent louche effect creates a mesmerizing visual."}'::jsonb, '{"created_year":"1935","origin":{"city":"Paris","country":"France"},"creator":{"name":"Ernest Hemingway","profession":"author"},"story":{"en":"Ernest Hemingway published this recipe in 1935 in a celebrity cocktail book called \"So Red the Nose, or Breath in the Afternoon.\" His instructions were characteristically terse: \"Pour one jigger absinthe into a Champagne glass. Add iced Champagne until it attains the proper opalescent milkiness. Drink three to five of these slowly.\" The drink shares its name with his 1932 novel about Spanish bullfighting, reflecting the author''s fascination with death, danger, and the Spanish culture."},"named_after":{"en":"Named after Ernest Hemingway''s 1932 novel \"Death in the Afternoon\" about Spanish bullfighting and the culture surrounding it."}}'::jsonb, '{"profile":["herbal","anise","champagne","complex"],"description":{"en":"Complex and dangerous. The absinthe provides intense anise, wormwood, and herbal notes that are softened and lifted by champagne''s effervescence. Sweet and dry simultaneously, with botanical complexity from the absinthe balanced by champagne''s acidity. Deceptively smooth and easy to drink."},"first_impression":{"en":"Champagne bubbles and anise hit first, elegant and bright"},"finish":{"en":"Long, herbal finish with lingering anise and wormwood notes"},"balance":{"en":"Champagne balances absinthe''s intensity, creating dangerous drinkability"}}'::jsonb, '{"best_time":["afternoon","evening","brunch"],"occasions":["celebration","literary_event","special_occasion","brunch"],"seasons":["spring","summer"],"food_pairings":{"en":"Best enjoyed on its own or with light appetizers like oysters, caviar, or delicate canapés. The strong flavors can overwhelm most foods, so keep pairings simple and elegant."},"ideal_for":{"en":"Perfect for literary enthusiasts and Hemingway fans. Ideal for adventurous drinkers who appreciate absinthe and champagne. Warning: extremely potent despite smooth taste. Hemingway''s suggestion of \"three to five\" should be approached with caution."}}'::jsonb, '[{"ingredient_id":"ING_ABSINTHE","quantity":{"amount":45,"unit":"ml"},"display_name":"Absinthe","notes":null},{"ingredient_id":"ING_CHAMPAGNE","quantity":{"amount":120,"unit":"ml"},"display_name":"Iced Champagne","notes":null}]'::jsonb, 'build', '{"en":"Pour absinthe into a chilled champagne flute. Top slowly with very cold champagne until the drink achieves a proper opalescent, milky appearance (the louche effect). No garnish needed. As Hemingway suggested: \"Drink three to five of these slowly.\""}'::jsonb, 'Champagne flute', '{"en":"None (traditional)"}'::jsonb, 'none', 'up', '{"ING_ABSINTHE"}', '{"herbal","anise","champagne","complex"}', 25, 200, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"celebration","literary_event","special_occasion","brunch"}', false, false, '{"death-in-the-gulf-stream","hemingway-champagne"}', 'CAUTION: Extremely high ABV. Champagne must be very cold. Pour slowly to achieve proper louche effect. Original Hemingway ratio was 1:5 absinthe to champagne. Monitor guest consumption carefully. Can substitute Pernod or Pastis if absinthe unavailable, but flavor will differ.', 'premium', 60, 'https://euvs-vintage-cocktail-books.cld.bz/1935-So-Red-the-Nose-or-Breath-in-the-Afternoon/23', 'Original recipe from "So Red the Nose, or Breath in the Afternoon" (1935) by Ernest Hemingway.', 1)
