-- 141. Division Bell


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('9c297a9a-eeb2-42a3-9720-665553667b99', 'division-bell', '423244758392084756382910485736291847563829', '{"en":"Division Bell"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","mezcal","aperol","herbal"}', '{"en":"A modern classic in the Last Word family, the Division Bell combines mezcal''s smoke with Aperol''s bitter orange and Chartreuse''s herbal complexity. Created by Phil Ward, this equal-parts cocktail is beautifully balanced, smoky, and refreshingly bitter-sweet with a distinctive pink-orange hue."}'::jsonb, '{"created_year":"2009","origin":{"city":"New York City","bar":"Mayahuel","country":"USA"},"creator":{"name":"Phil Ward","profession":"bartender"},"story":{"en":"Phil Ward created the Division Bell in 2009 at Mayahuel, a mezcal-focused bar in New York City. Following his successful Final Ward (rye variation of Last Word), Ward wanted to create another member of the Last Word family using mezcal. He combined mezcal with Aperol and Chartreuse in equal parts with lime, creating a drink that''s smoky, herbal, and bittersweet. The name references Pink Floyd''s album \"The Division Bell,\" inspired by the cocktail''s distinctive pink-orange color. It quickly became a modern classic and essential template for equal-parts cocktails."},"named_after":{"en":"Named after Pink Floyd''s 1994 album \"The Division Bell,\" inspired by the cocktail''s distinctive pink-orange color."}}'::jsonb, '{"profile":["smoky","bitter","herbal","citrus"],"description":{"en":"Complex and beautifully balanced. Mezcal provides earthy, smoky depth, while Aperol contributes bitter-sweet orange notes. Yellow Chartreuse adds herbal sweetness and complexity, and fresh lime brings brightness. The equal-parts formula creates a drink where smoke, bitter, herbal, and citrus elements harmonize perfectly. More approachable than the intense Final Ward, with a refreshing quality."},"first_impression":{"en":"Smoke and bitter orange hit first, followed by herbal and citrus notes"},"finish":{"en":"Medium finish with lingering smoke, herbs, and pleasant bitterness"},"balance":{"en":"Perfectly balanced equal-parts formula - smoke, bitter, herbal, citrus in harmony"}}'::jsonb, '{"best_time":["evening","aperitivo","cocktail_hour"],"occasions":["aperitivo","date_night","celebration","summer_gathering"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with Italian aperitivo foods like olives, cured meats, or bruschetta. Also pairs well with grilled seafood, Mexican cuisine, citrus-based dishes, and herbal salads."},"ideal_for":{"en":"Perfect for mezcal lovers and fans of the Last Word family. Ideal for those who enjoy bitter cocktails with complexity. Great for aperitivo hour and summer evenings. A must-try for anyone exploring modern classics."}}'::jsonb, '[{"ingredient_id":"ING_MEZCAL","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Mezcal","notes":null},{"ingredient_id":"ING_APEROL","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Aperol","notes":null},{"ingredient_id":"ING_YELLOW_CHARTREUSE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Yellow Chartreuse","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a chilled coupe or Nick & Nora glass. Garnish with a grapefruit twist."}'::jsonb, 'Coupe glass', '{"en":"Grapefruit twist"}'::jsonb, 'none', 'up', '{"ING_MEZCAL"}', '{"smoky","bitter","herbal","citrus"}', 25, 170, 'medium', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","date_night","celebration","summer_gathering"}', false, false, '{"last-word","final-ward","naked-and-famous"}', 'Equal parts formula - precision essential. Use Yellow Chartreuse, not Green. Fresh lime juice only. Part of the Last Word family alongside Final Ward and Naked & Famous. Pink-orange color is signature - presentation matters.', 'premium', 70, 'https://punchdrink.com/recipes/division-bell/', 'Created by Phil Ward at Mayahuel, New York City, 2009.', 1)
