-- 144. Final Ward


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('575a1c50-62da-42fe-b736-b8788b230008', 'final-ward', '524334849302847563829104857362918475638292', '{"en":"Final Ward"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","rye","herbal","bitter"}', '{"en":"A modern classic riff on the pre-Prohibition Last Word, the Final Ward swaps gin for rye whiskey while maintaining the equal-parts formula. Created by Phil Ward, this cocktail balances spicy rye with herbal Chartreuse, bitter Maraschino, and tart lemon, creating a complex, sophisticated drink."}'::jsonb, '{"created_year":"2007","origin":{"city":"New York City","bar":"Death & Co","country":"USA"},"creator":{"name":"Phil Ward","profession":"bartender"},"story":{"en":"Phil Ward created the Final Ward in 2007 at Death & Co, shortly after creating his other modern classic, the Oaxaca Old Fashioned. Ward wanted to create a rye whiskey variation of the cult-favorite Last Word (gin, Chartreuse, Maraschino, lime). By swapping gin for rye and lime for lemon, he created a drink that honors the original while being distinctly American. The name plays on \"Last Word\" while suggesting finality and authority. It became an instant hit and is now considered one of the essential modern classics."},"named_after":{"en":"Named as a play on \"Last Word\" (the original cocktail), suggesting both finality and the bartender''s ultimate statement on the formula."}}'::jsonb, '{"profile":["herbal","spicy","complex","balanced"],"description":{"en":"Intensely complex and perfectly balanced. Rye whiskey provides spicy, peppery notes that contrast beautifully with Green Chartreuse''s herbal sweetness. Maraschino adds almond-cherry complexity with a subtle bitterness, while lemon brings brightness. The equal-parts formula creates harmony where no single ingredient dominates. More assertive and whiskey-forward than the gin-based Last Word."},"first_impression":{"en":"Herbal Chartreuse and spicy rye hit first, followed by citrus and cherry notes"},"finish":{"en":"Long, complex finish with lingering herbal, spice, and almond notes"},"balance":{"en":"Perfectly balanced equal-parts formula - each ingredient contributes equally"}}'::jsonb, '{"best_time":["evening","cocktail_hour","late_night"],"occasions":["aperitivo","date_night","contemplative_drinking","special_occasion"],"seasons":["autumn","winter","spring","summer"],"food_pairings":{"en":"Excellent with charcuterie, aged cheeses, or herbal dishes. Also pairs well with duck, game meats, cherry-based desserts, and almond pastries."},"ideal_for":{"en":"Perfect for adventurous drinkers who appreciate complex, spirit-forward cocktails. Ideal for whiskey lovers exploring herbal liqueurs, or Last Word fans wanting a rye variation. Great for those who enjoy balanced, contemplative drinks."}}'::jsonb, '[{"ingredient_id":"ING_RYE_WHISKEY","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Rye whiskey","notes":null},{"ingredient_id":"ING_GREEN_CHARTREUSE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Green Chartreuse","notes":null},{"ingredient_id":"ING_MARASCHINO","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Maraschino liqueur","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a chilled coupe or Nick & Nora glass. Garnish with a lemon twist or brandied cherry."}'::jsonb, 'Coupe glass', '{"en":"Lemon twist or brandied cherry"}'::jsonb, 'none', 'up', '{"ING_RYE_WHISKEY"}', '{"herbal","spicy","complex","balanced"}', 28, 180, 'medium', 60, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter","spring","summer"}', '{"aperitivo","date_night","contemplative_drinking","special_occasion"}', false, false, '{"last-word","division-bell","naked-and-famous"}', 'Equal parts formula - precision matters. Use fresh lemon juice only. Green Chartreuse essential (not yellow). Shake hard to properly chill and dilute. Cousin to Last Word, Division Bell, and Naked & Famous.', 'premium', 75, 'https://www.deathandcompany.com/', 'Created by Phil Ward at Death & Co, New York City, 2007.', 1)
