-- 147. Fog Cutter


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('09c1f309-24f8-455c-ab94-2928acc3417b', 'fog-cutter', 'fog_cutter_famous_tiki_tropical_cocktail', '{"en":"Fog Cutter"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","strong","potent"}', '{"en":"The Fog Cutter is a powerful tiki cocktail created by Trader Vic, combining rum, gin, brandy, and sherry with citrus and orgeat. True to its name, this potent drink was designed to \"cut through the fog\" with its intense mix of spirits."}'::jsonb, '{"created_year":"1947","origin":{"city":"Oakland","bar":"Trader Vic''s","country":"USA"},"creator":{"name":"Victor \"Trader Vic\" Bergeron","profession":"bartender"},"story":{"en":"The Fog Cutter was created by Victor \"Trader Vic\" Bergeron in 1947 at his original restaurant in Oakland, California. Trader Vic was known for creating complex, multi-spirit tiki drinks, and the Fog Cutter represents one of his most potent inventions. The drink combines an unusual mix of rum, gin, brandy, and sherry - a combination that would be considered bizarre outside of tiki culture. Trader Vic himself warned that \"after two of these, you won''t see any fog,\" emphasizing the drink''s legendary strength. The cocktail became a cornerstone of the mid-century tiki craze and remains a classic test of bartending skill."},"named_after":{"en":"Named for its ability to \"cut through the fog\" - both the literal San Francisco fog and the mental fog from its potent mix of spirits."}}'::jsonb, '{"profile":["complex","citrus","strong"],"description":{"en":"Complex and powerful with layers of citrus, almond, and multiple spirits creating depth. The Fog Cutter balances bright lemon and orange with nutty orgeat and the warming complexity of rum, gin, brandy, and a sherry float. Deceptively smooth despite its strength."},"first_impression":{"en":"Bright citrus and almond notes hit first, masking the serious spirit content underneath"},"finish":{"en":"Long, warming finish with sherry complexity and lingering almond and citrus notes"},"balance":{"en":"Surprisingly balanced given the multiple spirits - citrus and orgeat create harmony despite the boozy foundation"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["tiki_bar","celebration","adventurous_drinking"],"seasons":["summer","autumn"],"food_pairings":{"en":"Perfect with Polynesian-style dishes, teriyaki chicken, grilled pork, or Chinese spare ribs. Also pairs well with rich, fatty foods that can stand up to the drink''s intensity."},"ideal_for":{"en":"Perfect for adventurous drinkers and tiki enthusiasts seeking an authentic Trader Vic experience. Ideal for those who appreciate complex, spirit-forward cocktails. Warning: extremely potent - pace yourself."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":45,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_GIN","quantity":{"amount":15,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_BRANDY","quantity":{"amount":15,"unit":"ml"},"display_name":"Brandy","notes":null},{"ingredient_id":"ING_LEMON_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh lemon juice","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_ORGEAT","quantity":{"amount":15,"unit":"ml"},"display_name":"Orgeat syrup","notes":null},{"ingredient_id":"ING_SHERRY_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Cream sherry (float)","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients except sherry to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a fog cutter glass or tall tiki mug filled with crushed ice. Carefully float the cream sherry on top. Garnish with mint sprig."}'::jsonb, 'Fog cutter glass / Tall tiki mug', '{"en":"Fresh mint sprig, orange slice"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_WHITE","ING_GIN","ING_BRANDY"}', '{"complex","citrus","strong"}', 18, 260, 'medium', 120, '{"sulphites","tree_nuts"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","pescatarian","gluten_free","dairy_free"}', 0, '{"vegetarian","gluten-free","dairy-free"}', '{"summer","autumn"}', '{"tiki_bar","celebration","adventurous_drinking"}', false, false, '{"trader-vics-fog-cutter"}', 'One of Trader Vic''s most potent creations. The sherry float is essential - pour carefully over the back of a spoon. Orgeat contains almonds. Warn guests: "Two of these and you won''t see any fog." Very strong - limit to 1-2 per guest.', 'premium', 65, 'https://tradervics.com/cocktails/', 'Original Trader Vic recipe from 1947. Trader Vic''s Bartender''s Guide. Tiki cocktail archives.', 1)
