-- 148. Fuzzy Navel


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('ab135f05-9484-4ac0-b49f-a527f9e9bf8f', 'fuzzy-navel', 'f5a6b7c8d9e0f1a2b3c4d5e6f7a8b9c0d1e2f3a4', '{"en":"Fuzzy Navel"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","peach","orange","sweet","1980s","party"}', '{"en":"A sweet and simple cocktail combining peach schnapps with fresh orange juice. The Fuzzy Navel became a sensation in the 1980s with its approachable flavor and playful name, making it a staple at parties and bars worldwide."}'::jsonb, '{"created_year":"1984","origin":{"city":"United States","country":"USA"},"story":{"en":"The Fuzzy Navel was created in 1984 by National Distillers as a marketing vehicle for DeKuyper Peachtree Schnapps. The name is a playful reference to the \"fuzzy\" peach skin and \"navel\" orange. It became an instant hit, particularly among young adults and college students, during the 1980s schnapps craze. The drink''s simplicity (just two ingredients) and sweet, non-threatening flavor made it extremely popular. It spawned numerous variations and helped establish peach schnapps as a bar staple."},"named_after":{"en":"Named for the \"fuzzy\" skin of a peach and \"navel\" orange, creating a playful, memorable name that contributed to its popularity."}}'::jsonb, '{"profile":["sweet","fruity","peach","orange"],"description":{"en":"Sweet and intensely fruity with dominant peach and orange flavors. Very smooth and easy-drinking with minimal alcohol burn, making it almost dangerously drinkable. The peach schnapps provides sweetness and peach flavor while the orange juice adds fresh citrus notes."},"first_impression":{"en":"Sweet peach and orange burst, very fruity and smooth"},"finish":{"en":"Sweet, smooth finish with lingering peach and orange notes"},"balance":{"en":"Heavily sweet-leaning with no tartness to balance - pure fruit sweetness throughout"}}'::jsonb, '{"best_time":["afternoon","evening","brunch"],"occasions":["party","casual","poolside","celebration"],"seasons":["summer","spring"],"food_pairings":{"en":"Best as a standalone party drink. Can pair with light brunch items, fruit plates, and sweet breakfast pastries. Works with salty snacks that contrast the sweetness."},"ideal_for":{"en":"Perfect for those who prefer sweet, non-alcoholic-tasting drinks. Great for people new to cocktails or anyone who wants a fun, easy-drinking party beverage. Popular with younger crowds."}}'::jsonb, '[{"ingredient_id":"ING_PEACH_SCHNAPPS","quantity":{"amount":45,"unit":"ml"},"display_name":"Peach schnapps","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":120,"unit":"ml"},"display_name":"Fresh orange juice","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour peach schnapps over ice, then top with fresh orange juice. Stir gently to combine. Garnish with an orange slice and peach slice if available."}'::jsonb, 'Highball glass', '{"en":"Orange slice, optional peach slice"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_PEACH_SCHNAPPS"}', '{"sweet","fruity","peach","orange"}', 8, 180, 'easy', 25, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"summer","spring"}', '{"party","casual","poolside"}', false, false, '{"hairy-navel","woo-woo","sex-on-the-beach"}', 'Add vodka to make it a "Hairy Navel". Use fresh orange juice for best flavor. Very popular with college-age crowds. Quality peach schnapps recommended - avoid artificial flavors.', 'low', 80, 'https://www.diffordsguide.com/cocktails/recipe/1241/fuzzy-navel', 'Created in 1984 by National Distillers for DeKuyper Peachtree Schnapps marketing.', 1)
