-- 153. Golden Cadillac


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('8432cb47-9ad0-4d01-8752-af6313a32861', 'golden-cadillac', 'golden_cadillac_galliano_cacao_cream', '{"en":"Golden Cadillac"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","vintage","chocolate"}', '{"en":"A luxurious, golden-hued dessert cocktail combining Galliano, white crème de cacao, and cream. This vintage classic from the 1950s delivers a unique blend of herbal vanilla notes from Galliano with sweet chocolate cream."}'::jsonb, '{"created_year":"1952","origin":{"city":"El Dorado","bar":"Poor Red''s BBQ","country":"USA"},"creator":{"name":"Frank Klein","profession":"bartender"},"story":{"en":"The Golden Cadillac was created in 1952 by bartender Frank Klein at Poor Red''s BBQ in El Dorado, California. According to legend, a couple came into the bar after purchasing a new Cadillac and asked Klein to create a drink to celebrate. Klein mixed Galliano, white crème de cacao, and cream, creating a golden cocktail that matched the color of luxury Cadillacs of that era. The drink became a sensation in the 1950s-60s during the golden age of creamy cocktails."},"named_after":{"en":"Named after the luxury Cadillac automobile, referencing both the golden color and the luxurious, premium nature of the drink."}}'::jsonb, '{"profile":["creamy","sweet","herbal","vanilla"],"description":{"en":"Sweet and creamy with distinctive herbal-vanilla notes from Galliano balanced by white chocolate sweetness. Smooth and indulgent with a unique flavor profile that sets it apart from other creamy cocktails."},"first_impression":{"en":"Creamy vanilla sweetness with immediate herbal complexity from Galliano"},"finish":{"en":"Smooth, lingering vanilla and anise notes with creamy texture"},"balance":{"en":"Well-balanced between herbal complexity and creamy sweetness - more interesting than simple chocolate cream drinks"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["digestivo","retro_party","celebration","nightcap"],"seasons":["autumn","winter","all_seasons"],"food_pairings":{"en":"Pairs beautifully with vanilla desserts, panna cotta, white chocolate mousse, or tiramisu. Also complements almond biscotti and shortbread cookies."},"ideal_for":{"en":"Perfect for those who enjoy vintage cocktails and appreciate Galliano''s unique flavor. Ideal for retro-themed events and anyone seeking a sophisticated dessert drink with historical charm. Great for Cadillac owners!"}}'::jsonb, '[{"ingredient_id":"ING_GALLIANO","quantity":{"amount":30,"unit":"ml"},"display_name":"Galliano","notes":null},{"ingredient_id":"ING_CREME_DE_CACAO_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White crème de cacao","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 10-15 seconds until well-chilled and frothy. Strain into a chilled coupe or champagne saucer. The drink should have a beautiful golden hue."}'::jsonb, 'Coupe (or Champagne saucer)', '{"en":"None (golden color is the visual appeal)"}'::jsonb, 'none', 'up', '{"ING_GALLIANO"}', '{"creamy","sweet","herbal","vanilla"}', 15, 260, 'easy', 60, '{"milk"}', '{"lactose","alcohol"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"autumn","winter","all-seasons"}', '{"digestivo","celebration","nightcap"}', false, false, '{"golden-dream","yellow-cadillac","pink-cadillac"}', 'Classic 1950s cocktail. Galliano is essential for authentic flavor - do not substitute. Some modern recipes add orange juice, but original is just the three ingredients. Can be served frozen for a more dessert-like experience. The golden color should be prominent.', 'mid', 45, 'https://www.diffordsguide.com/cocktails/recipe/1313/golden-cadillac', 'Classic 1950s dessert cocktail from California.', 1)
