-- 154. Goombay Smash


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('3b81a2b0-55bb-4fd7-8085-078b51bb839b', 'goombay-smash', 'goombay-smash-tiki-tropical-famous-2025', '{"en":"Goombay Smash"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","bahamas","fruity","signature"}', '{"en":"The signature cocktail of the Bahamas, combining coconut rum with apricot brandy and tropical fruit juices. The Goombay Smash is a potent yet refreshing blend that captures the spirit of island celebration and Caribbean culture."}'::jsonb, '{"created_year":"1960s","origin":{"city":"Green Turtle Cay","bar":"Miss Emily''s Blue Bee Bar","country":"Bahamas"},"creator":{"name":"Emily Cooper","profession":"bartender"},"story":{"en":"The Goombay Smash was created by Miss Emily Cooper at her legendary Blue Bee Bar on Green Turtle Cay in the Abaco Islands, Bahamas, during the 1960s. The drink became so popular that it''s now considered the national cocktail of the Bahamas. \"Goombay\" refers to a style of Bahamian music and dance with African roots, celebrating the islands'' cultural heritage. Miss Emily''s original recipe was kept secret for years, and she famously made each drink herself, refusing to let anyone else prepare it. The cocktail gained international fame and is now served throughout the Caribbean, though purists insist the best version is still made at the original Blue Bee Bar."},"named_after":{"en":"Named after \"Goombay,\" a traditional Bahamian music and dance style with African origins, celebrating the islands'' rich cultural heritage and festive spirit."}}'::jsonb, '{"profile":["fruity","tropical","sweet"],"description":{"en":"Complex and tropical with layers of coconut, apricot, pineapple, and citrus. The coconut rum provides a sweet, tropical base while apricot brandy adds depth and sophistication. Dangerously smooth and deceptively strong."},"first_impression":{"en":"Sweet coconut and tropical fruit burst with subtle apricot complexity"},"finish":{"en":"Long, warming finish with lingering coconut, apricot, and rum notes"},"balance":{"en":"Well-balanced sweetness with tropical complexity - the apricot brandy prevents it from being one-dimensional"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","celebration","vacation","caribbean_theme","special_occasion"],"seasons":["summer","year_round"],"food_pairings":{"en":"Perfect with Bahamian conch fritters, grilled lobster, coconut shrimp, jerk chicken, or tropical fruit platters. Also pairs well with spicy Caribbean dishes as the sweetness balances heat."},"ideal_for":{"en":"Perfect for Caribbean vacations, island celebrations, and tropical beach parties. Ideal for those seeking the authentic taste of the Bahamas. A must-try for tiki cocktail enthusiasts and rum lovers."}}'::jsonb, '[{"ingredient_id":"ING_RUM_COCONUT","quantity":{"amount":45,"unit":"ml"},"display_name":"Coconut rum","notes":null},{"ingredient_id":"ING_APRICOT_BRANDY","quantity":{"amount":15,"unit":"ml"},"display_name":"Apricot brandy","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":10,"unit":"ml"},"display_name":"Simple syrup","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well chilled. Strain into a hurricane glass filled with fresh ice. Garnish with pineapple wedge and cherry."}'::jsonb, 'Hurricane glass', '{"en":"Pineapple wedge and maraschino cherry"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_RUM_COCONUT"}', '{"fruity","tropical","sweet"}', 14, 240, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"summer","year_round"}', '{"beach","celebration","vacation","caribbean_theme"}', false, false, '{"frozen-goombay-smash","virgin-goombay-smash"}', 'National cocktail of the Bahamas. Original recipe from Miss Emily''s Blue Bee Bar. The apricot brandy is key to authentic flavor. Can be served frozen. Deceptively strong - warn guests. Some recipes include rum floater on top.', 'mid', 70, 'https://www.liquor.com/recipes/goombay-smash/', 'National cocktail of Bahamas. Created by Miss Emily Cooper at Blue Bee Bar.', 1)
