-- 157. Hemingway Daiquiri


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('2489976e-a8b6-4871-8122-919eae330b4e', 'hemingway-daiquiri', 'd0e1f2a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9', '{"en":"Hemingway Daiquiri"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","rum","tart","cuban","literary"}', '{"en":"A tart, sugar-free variation of the classic daiquiri featuring white rum, lime, grapefruit, and maraschino liqueur. Created for Ernest Hemingway, this cocktail is bright, refreshing, and perfectly balanced without added sugar."}'::jsonb, '{"created_year":"1930","origin":{"city":"Havana","bar":"El Floridita","country":"Cuba"},"creator":{"name":"Constantino Ribalaigua","profession":"bartender"},"story":{"en":"Created at Havana''s legendary El Floridita bar by head bartender Constantino Ribalaigua for Ernest Hemingway in the 1930s. Hemingway, a diabetic, requested a daiquiri without sugar. Constantino crafted this tart masterpiece using double rum, fresh citrus, and maraschino liqueur. Also known as \"Papa Doble\" (Hemingway''s nickname), this drink became his signature order and helped make El Floridita famous worldwide."},"named_after":{"en":"Named after Ernest Hemingway, the legendary American author who lived in Cuba and frequented El Floridita. Also called \"Papa Doble\" after his nickname."}}'::jsonb, '{"profile":["tart","citrus","refreshing"],"description":{"en":"Bright and intensely tart with fresh lime and grapefruit dominating. The maraschino liqueur adds subtle cherry sweetness and complexity, while double rum provides backbone. Refreshing and bracing without sweetness."},"first_impression":{"en":"Sharp citrus tartness hits immediately with grapefruit and lime brightness"},"finish":{"en":"Clean, refreshing finish with lingering citrus and subtle maraschino"},"balance":{"en":"Boldly tart yet balanced - the maraschino provides just enough sweetness to round sharp citrus"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["aperitivo","beach","summer_party","brunch"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with fresh seafood, ceviche, oysters, or light salads. Perfect as a palate-cleansing aperitif before rich meals."},"ideal_for":{"en":"Perfect for those who prefer tart, sugar-free cocktails. Ideal for rum lovers seeking a bright, refreshing drink without sweetness, or anyone following Hemingway''s legendary example."}}'::jsonb, '[{"ingredient_id":"ING_WHITE_RUM","quantity":{"amount":60,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh grapefruit juice","notes":null},{"ingredient_id":"ING_MARASCHINO_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Maraschino liqueur","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe or cocktail glass. Garnish with a lime wheel or grapefruit twist."}'::jsonb, 'Coupe glass', '{"en":"Lime wheel or grapefruit twist"}'::jsonb, 'none', 'up', '{"ING_WHITE_RUM"}', '{"tart","citrus","refreshing"}', 20, 140, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free","sugar_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free","sugar-free"}', '{"spring","summer"}', '{"aperitivo","beach","summer_party","brunch"}', false, false, '{"papa-doble","hemingway-special","daiquiri-natural"}', 'No sugar added - Hemingway was diabetic. Double rum makes this strong. Use fresh grapefruit juice only. Luxardo maraschino is preferred. Legend says Hemingway drank these in quantities of 6-12 per session. Serve very cold.', 'mid', 75, 'https://www.diffordsguide.com/cocktails/recipe/1375/hemingway-daiquiri', 'Created at El Floridita, Havana, 1930s. Hemingway''s signature drink, also known as Papa Doble.', 1)
