-- 164. Jäger Bomb


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('6436398e-0b23-4f0d-9122-d05959118255', 'jager-bomb', 'jager_bomb_shot_famous_energy_drink_bomb', '{"en":"Jäger Bomb"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","bomb","energy-drink","party","herbal"}', '{"en":"A high-energy bomb shot where Jägermeister is dropped into a glass of Red Bull or energy drink. This iconic party drink combines the herbal liqueur with caffeine for an intense, energizing experience that became a nightlife phenomenon."}'::jsonb, '{"created_year":"2000s","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"story":{"en":"The Jäger Bomb exploded in popularity in the early 2000s in American bars and nightclubs. While bomb shots existed before, the specific combination of Jägermeister and Red Bull became a cultural phenomenon, particularly in the electronic dance music scene. The drink''s popularity was so significant that it influenced nightlife culture worldwide and spawned numerous variations."},"named_after":{"en":"Named after Jägermeister and the \"bomb shot\" style of dropping a shot glass into a larger drink."}}'::jsonb, '{"profile":["herbal","sweet","energizing"],"description":{"en":"Complex herbal flavors from Jägermeister blend with the sweet, citrusy energy drink. The 56 herbs and spices in Jäger create a unique profile that''s both medicinal and sweet, enhanced by the Red Bull''s taurine and caffeine kick."},"first_impression":{"en":"Sweet energy drink followed by intense herbal Jäger notes"},"finish":{"en":"Energizing finish with lingering herbal complexity and caffeine buzz"},"balance":{"en":"Energy drink sweetness balances Jägermeister''s herbal intensity"}}'::jsonb, '{"best_time":["late_night"],"occasions":["party","nightlife","clubbing","celebration"],"seasons":["all_seasons"],"food_pairings":{"en":"Not typically paired with food. Best consumed as a standalone party shot or nightclub drink."},"ideal_for":{"en":"Perfect for high-energy nightlife situations and late-night partying. Popular among clubbers and EDM fans. WARNING: Contains high amounts of caffeine and alcohol - consume responsibly."}}'::jsonb, '[{"ingredient_id":"ING_JAGERMEISTER","quantity":{"amount":45,"unit":"ml"},"display_name":"Jägermeister","notes":null},{"ingredient_id":"ING_RED_BULL","quantity":{"amount":120,"unit":"ml"},"display_name":"Red Bull (or energy drink)","notes":null}]'::jsonb, 'build', '{"en":"Fill a pint glass halfway with Red Bull or energy drink. Pour Jägermeister into a shot glass. Drop the shot glass into the pint glass and consume immediately in one go."}'::jsonb, 'Shot glass (dropped into pint glass)', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_JAGERMEISTER"}', '{"herbal","sweet","energizing"}', 12, 200, 'easy', 30, '{"sulphites","caffeine"}', '{"alcohol","sulphites_intolerance","caffeine_sensitivity"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"all-seasons"}', '{"party","nightlife","clubbing"}', false, false, '{"jagerbomb-original","skip-and-go-naked","starry-night"}', 'WARNING: High caffeine and alcohol content. Serve responsibly and be aware of mixing stimulants with depressants. Some regions have restrictions on serving these drinks. Ensure shot glass is sturdy to prevent breakage when dropped. Can substitute Red Bull with other energy drinks.', 'mid', 95, 'https://en.wikipedia.org/wiki/Jägerbomb', 'Iconic 2000s bomb shot combining Jägermeister and energy drink.', 1)
