-- 165. Jet Pilot


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('abf033ad-cd6c-4786-99fa-a7ddb0f33729', 'jet-pilot', 'jet_pilot_famous_tiki_tropical_cocktail', '{"en":"Jet Pilot"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","potent","spiced"}', '{"en":"The Jet Pilot is an intensely potent tiki cocktail, a turbocharged variation of the Test Pilot with even more overproof rum and aggressive spicing. This drink lives up to its jet-powered name with serious strength and complex layers of flavor."}'::jsonb, '{"created_year":"1958","origin":{"city":"Hollywood","bar":"Don the Beachcomber","country":"USA"},"creator":{"name":"Ernest Raymond Beaumont Gantt (Donn Beach)","profession":"bartender"},"story":{"en":"The Jet Pilot was created around 1958 by Don the Beachcomber as an evolution of his earlier Test Pilot cocktail. As jet aviation was revolutionizing air travel in the late 1950s, Don updated his aviation-themed drink for the jet age. The Jet Pilot takes the already complex Test Pilot formula and amplifies it with more overproof rum and additional falernum, creating an even more potent and spice-forward drink. The cocktail reflects the optimism and power of the jet age while maintaining the complexity that defines Don the Beachcomber''s style. Like many of Don''s recipes, it remained a closely guarded secret until tiki historian Jeff \"Beachbum\" Berry decoded it decades later."},"named_after":{"en":"Named after jet pilots and the jet age of the late 1950s, symbolizing power, speed, and modern sophistication."}}'::jsonb, '{"profile":["potent","spiced","complex"],"description":{"en":"Explosively complex and dangerously potent with aggressive rum and spice character. The Jet Pilot delivers intense layers of overproof rum heat, warming spices from falernum, subtle anise, citrus brightness, and almond notes. This is tiki drinking at its most extreme."},"first_impression":{"en":"Powerful rum and spice hit immediately, with citrus trying to tame the beast"},"finish":{"en":"Very long, intensely warming finish with persistent spice and rum heat"},"balance":{"en":"Boldly unbalanced in the best way - this is about power and intensity, not subtlety"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["tiki_bar","adventurous_drinking","special_occasion"],"seasons":["year_round"],"food_pairings":{"en":"Perfect with rich, spicy foods that can stand up to the intensity - jerk chicken, spicy ribs, blackened fish, or bold curries. The strong flavors complement equally strong food."},"ideal_for":{"en":"Perfect for hardcore tiki enthusiasts and thrill-seekers. NOT for casual drinkers or beginners. This is an extreme cocktail for those who want maximum intensity. Limit to one - seriously potent. Advanced drinkers only."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":45,"unit":"ml"},"display_name":"Dark rum (Jamaican)","notes":null},{"ingredient_id":"ING_RUM_DEMERARA","quantity":{"amount":30,"unit":"ml"},"display_name":"Demerara rum (151 proof)","notes":null},{"ingredient_id":"ING_FALERNUM","quantity":{"amount":15,"unit":"ml"},"display_name":"Falernum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRAPEFRUIT_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh grapefruit juice","notes":null},{"ingredient_id":"ING_CINNAMON_SYRUP","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Cinnamon syrup","notes":null},{"ingredient_id":"ING_PERNOD","quantity":{"amount":6,"unit":"ml"},"display_name":"Pernod (absinthe)","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":3,"unit":"dash"},"display_name":"Angostura bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake very vigorously until extremely well-chilled. Strain into a double old-fashioned glass or tiki mug filled with crushed ice. Garnish with mint sprig and lime wheel."}'::jsonb, 'Double old-fashioned / Tiki mug', '{"en":"Fresh mint sprig, lime wheel, cinnamon stick"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_DARK","ING_RUM_DEMERARA"}', '{"potent","spiced","complex"}', 26, 250, 'hard', 120, '{"sulphites","wormwood"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 3, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"year_round"}', '{"tiki_bar","adventurous_drinking","special_occasion"}', false, false, '{"don-the-beachcomber-jet-pilot"}', 'EXTREMELY POTENT - warn guests and limit to 1 per person. Created by Don the Beachcomber circa 1958. More aggressive than Test Pilot with double the Pernod and additional falernum. 151-proof Demerara is essential. This is an extreme cocktail - handle with care.', 'premium', 58, 'https://beachbumberry.com/jet-pilot.html', 'Original Don the Beachcomber recipe circa 1958, decoded by Jeff "Beachbum" Berry in Sippin'' Safari.', 1)
