-- 168. Kentucky Mule


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('399b0a47-d829-416f-98f5-e7141f1dd120', 'kentucky-mule', 'a3b2c1d0e9f8182736455463728192021222324252', '{"en":"Kentucky Mule"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","bourbon","ginger","refreshing"}', '{"en":"A bourbon-based variation of the classic Moscow Mule, the Kentucky Mule combines bourbon whiskey with spicy ginger beer and fresh lime juice. Served in the traditional copper mug, this refreshing highball showcases how bourbon''s vanilla and caramel notes harmonize beautifully with ginger''s spice."}'::jsonb, '{"created_year":"1940s-1950s","origin":{"city":"Kentucky","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Kentucky Mule emerged as a natural variation of the Moscow Mule, which was created in the 1940s. As the Moscow Mule gained popularity, bartenders across America began substituting vodka with their local spirits. In Kentucky, the bourbon capital of the United States, it was only natural to create a version with bourbon. The drink gained mainstream popularity in the 2010s during the craft cocktail renaissance, becoming a go-to bourbon cocktail for those who find Old Fashioneds too strong."},"named_after":{"en":"Named after Kentucky, the bourbon capital of the United States, replacing vodka (Moscow) with bourbon (Kentucky) in the Mule formula."}}'::jsonb, '{"profile":["ginger","bourbon","citrus","refreshing"],"description":{"en":"Refreshing and complex. Bourbon''s vanilla, oak, and caramel notes are lifted by spicy ginger beer, while fresh lime adds bright acidity. The copper mug keeps it ice cold, emphasizing the refreshing quality. Ginger''s spice complements bourbon''s warmth beautifully."},"first_impression":{"en":"Spicy ginger and bright lime hit first, bourbon warmth follows"},"finish":{"en":"Clean, refreshing finish with lingering ginger spice and bourbon notes"},"balance":{"en":"Well-balanced - ginger spice cuts bourbon''s sweetness, lime adds brightness"}}'::jsonb, '{"best_time":["afternoon","evening","happy_hour"],"occasions":["casual","party","bbq","summer_gathering"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with barbecue, fried chicken, burgers, or spicy foods. Also pairs well with Asian cuisine, particularly Thai or Vietnamese dishes, and pub fare like wings or nachos."},"ideal_for":{"en":"Perfect for bourbon lovers who want something refreshing and easy-drinking. Ideal for those who enjoy Moscow Mules but prefer whiskey to vodka. Great for hot weather and casual gatherings."}}'::jsonb, '[{"ingredient_id":"ING_BOURBON","quantity":{"amount":60,"unit":"ml"},"display_name":"Bourbon whiskey","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GINGER_BEER","quantity":{"amount":120,"unit":"ml"},"display_name":"Ginger beer","notes":null}]'::jsonb, 'build', '{"en":"Fill a copper mug or highball glass with ice. Add bourbon and lime juice. Top with ginger beer and stir gently to combine. Garnish with a lime wedge and mint sprig."}'::jsonb, 'Copper mug (or Highball glass)', '{"en":"Lime wedge and mint sprig"}'::jsonb, 'cubes', 'highball', '{"ING_BOURBON"}', '{"ginger","bourbon","citrus","refreshing"}', 12, 200, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance","ginger_intolerance"}', '{"vegetarian","vegan","gluten_free","dairy_free","nut_free"}', 1, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"casual","party","bbq","summer_gathering"}', false, false, '{"spicy-kentucky-mule","apple-kentucky-mule","smoky-kentucky-mule"}', 'Use quality ginger beer with real ginger - not ginger ale. Copper mug preferred for presentation and temperature. Can muddle mint for extra freshness. Fresh lime juice essential.', 'mid', 85, 'https://www.liquor.com/recipes/kentucky-mule/', 'Modern variation of Moscow Mule, popularized in 2010s.', 1)
