-- 173. Lucien Gaudin


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('0013b800-3144-4122-ba41-71b3321d6648', 'lucien-gaudin', 'a3b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2', '{"en":"Lucien Gaudin"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","gin","complex","elegant"}', '{"en":"An elegant cocktail combining gin, Cointreau, Campari, and dry vermouth. The Lucien Gaudin is a sophisticated balance of botanical, bitter, and citrus notes, creating a complex aperitif with beautiful color."}'::jsonb, '{"created_year":"1929","origin":{"city":"Paris","bar":"Unknown","country":"France"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"Created in Paris around 1929 and named after Lucien Gaudin, a legendary French Olympic fencer who won gold medals in 1924 and 1928. This cocktail represents the glamorous cocktail culture of 1920s Paris. The drink balances Italian bitterness (Campari) with French sophistication (Cointreau and vermouth) and British botanicals (gin), representing international cocktail culture."},"named_after":{"en":"Named after Lucien Gaudin (1886-1934), French Olympic fencer and two-time gold medalist who was celebrated as a national hero in 1920s France."}}'::jsonb, '{"profile":["bitter","complex","citrus"],"description":{"en":"Sophisticated and complex with Campari''s distinctive bitterness balanced by Cointreau''s orange sweetness, dry vermouth''s botanicals, and gin''s juniper notes. Elegant and aperitif-perfect with beautiful rose-orange color."},"first_impression":{"en":"Campari bitterness and orange sweetness hit together with botanical gin notes"},"finish":{"en":"Dry, elegant finish with lingering bitterness and citrus complexity"},"balance":{"en":"Perfectly balanced between bitter and sweet - complex yet approachable aperitif"}}'::jsonb, '{"best_time":["afternoon","early_evening"],"occasions":["aperitivo","pre_dinner","date_night","celebration"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with Italian antipasti, charcuterie, olives, or light Mediterranean dishes. Perfect as a pre-dinner aperitif to stimulate the appetite."},"ideal_for":{"en":"Perfect for Negroni lovers seeking elegant variations. Ideal for those who appreciate bitter aperitifs with citrus complexity and sophisticated balance."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_COINTREAU","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Cointreau","notes":null},{"ingredient_id":"ING_CAMPARI","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Campari","notes":null},{"ingredient_id":"ING_DRY_VERMOUTH","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Dry vermouth","notes":null}]'::jsonb, 'stir', '{"en":"Add all ingredients to a mixing glass filled with ice. Stir gently for 20-30 seconds until well-chilled. Strain into a chilled coupe or cocktail glass. Garnish with an orange twist, expressing the oils over the drink."}'::jsonb, 'Coupe glass', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"bitter","complex","citrus"}', 26, 170, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"aperitivo","pre_dinner","date_night","celebration"}', false, false, '{"lucien-gaudin-moderne"}', 'Equal parts after the gin creates beautiful balance. Use quality dry vermouth. The drink should be rose-orange colored. Great alternative for Negroni lovers seeking something more delicate. Serve very cold.', 'mid', 55, 'https://www.diffordsguide.com/cocktails/recipe/1427/lucien-gaudin', 'Created in 1920s Paris. Named after French Olympic fencing champion Lucien Gaudin.', 1)
