-- 177. Maid in Cuba


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('182f3d74-fec3-4f24-a0f6-0422805a6077', 'maid-in-cuba', 'e9d8c7b6a5940382817069584736251faecdbf02', '{"en":"Maid in Cuba"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","refreshing","minty","tropical"}', '{"en":"A refreshing hybrid of a Mojito and a Whiskey Smash, the Maid in Cuba combines aged rum with fresh mint, lime, and cucumber. This modern classic bridges Cuban cocktail tradition with contemporary craft bartending."}'::jsonb, '{"created_year":"2008","origin":{"city":"New York City","bar":"Pegu Club","country":"USA"},"creator":{"name":"Audrey Saunders","profession":"bartender"},"story":{"en":"Created by legendary bartender Audrey Saunders at Pegu Club in 2008, the Maid in Cuba was inspired by a request for a \"better Mojito.\" Saunders elevated the classic Cuban cocktail by using aged rum instead of white rum and adding muddled cucumber for extra refreshment. The name cleverly references both the \"Maid\" drinks family and Cuban rum tradition."},"named_after":{"en":"A play on words combining \"Maid\" (as in Whiskey Smash variations) with \"Made in Cuba,\" referencing the Cuban rum tradition."}}'::jsonb, '{"profile":["refreshing","minty","citrus"],"description":{"en":"Refreshing and herbaceous with a sophisticated rum backbone. The aged rum provides depth and richness, while fresh mint and cucumber create cooling, garden-fresh flavors. Lime adds bright acidity, and a touch of simple syrup balances it all."},"first_impression":{"en":"Cool cucumber and mint hit first, followed by warm aged rum and citrus brightness"},"finish":{"en":"Clean, refreshing finish with lingering mint and subtle rum warmth"},"balance":{"en":"Perfectly balanced between refreshing herbs and spirit depth, neither too sweet nor too strong"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["summer_party","brunch","poolside","casual_gathering"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Cuban sandwiches, ceviche, grilled fish, fresh salads, and light seafood dishes. The refreshing character complements tropical and Latin cuisine."},"ideal_for":{"en":"Perfect for those who love Mojitos but want something more sophisticated. Ideal for rum enthusiasts and anyone seeking a refreshing, herb-forward cocktail."}}'::jsonb, '[{"ingredient_id":"ING_RUM_AGED","quantity":{"amount":60,"unit":"ml"},"display_name":"Aged rum","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_MINT_LEAVES","quantity":{"amount":8,"unit":"leaves"},"display_name":"Fresh mint leaves","notes":null},{"ingredient_id":"ING_CUCUMBER","quantity":{"amount":3,"unit":"slices"},"display_name":"Cucumber slices","notes":null}]'::jsonb, 'muddle_shake', '{"en":"In a shaker, gently muddle mint leaves and cucumber slices. Add rum, lime juice, simple syrup, and ice. Shake well until chilled. Double strain into a rocks glass filled with fresh ice. Garnish with a cucumber ribbon and mint sprig."}'::jsonb, 'Rocks glass', '{"en":"Cucumber ribbon and fresh mint sprig"}'::jsonb, 'cubes', 'on_rocks', '{"ING_RUM_AGED"}', '{"refreshing","minty","citrus"}', 14, 165, 'medium', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"summer_party","brunch","poolside","casual_gathering"}', false, false, '{"mojito","whiskey-smash"}', 'Muddle gently to avoid bitterness from mint. Use quality aged rum for best results. Double strain to remove mint and cucumber fragments. Can garnish with edible flowers for special occasions.', 'mid', 78, 'https://peguclub.com', 'Created by Audrey Saunders at Pegu Club, 2008. Modern classic cocktail.', 1)
