-- 185. Orgasm


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('e929f5bc-0cd2-4dcf-b8ca-92661e6180f4', 'orgasm', 'orgasm_amaretto_kahlua_baileys_cream', '{"en":"Orgasm"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","shooter","sweet","coffee","nutty"}', '{"en":"A decadent shooter combining amaretto, coffee liqueur, Irish cream, and cream. This infamous shot delivers an explosion of sweet, creamy flavors with notes of almond, coffee, and chocolate in one indulgent gulp."}'::jsonb, '{"created_year":"1980s","origin":{"country":"USA"},"story":{"en":"The Orgasm shot emerged in the 1980s during the height of the shooter craze in American bars. Its provocative name and intensely sweet, creamy flavor made it an instant hit at parties and nightclubs. The drink became part of a family of similarly-named shots (Screaming Orgasm, Multiple Orgasm, etc.) that dominated bar culture in the late 1980s and 1990s. While some consider the name dated, the delicious combination of amaretto, coffee liqueur, and cream remains popular as both a shot and a cocktail."},"named_after":{"en":"The provocative name refers to the intensely pleasurable, indulgent experience of drinking this sweet, creamy shot."}}'::jsonb, '{"profile":["creamy","sweet","coffee","nutty"],"description":{"en":"Intensely sweet and creamy with harmonious notes of almond, coffee, and chocolate. The amaretto provides nutty sweetness, the Kahlúa adds coffee depth, and the Baileys and cream create a silky, indulgent texture. Very dessert-forward and smooth."},"first_impression":{"en":"Sweet cream explosion with immediate almond and coffee notes"},"finish":{"en":"Lingering sweetness with coffee, almond, and cream"},"balance":{"en":"Very sweet and indulgent - designed for pleasure rather than balance"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","bachelor_party","casual"],"seasons":["all_seasons"],"food_pairings":{"en":"Typically consumed as a standalone shot. Can accompany desserts or serve as a liquid dessert itself."},"ideal_for":{"en":"Perfect for those who enjoy very sweet, creamy shots. Ideal for parties, celebrations, and social drinking occasions. Popular with guests seeking an indulgent, dessert-like shot experience."}}'::jsonb, '[{"ingredient_id":"ING_AMARETTO","quantity":{"amount":15,"unit":"ml"},"display_name":"Amaretto","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null},{"ingredient_id":"ING_IRISH_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Irish cream (Baileys)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake well for 10 seconds until chilled. Strain into a shot glass. Shoot in one gulp or sip slowly to enjoy the flavors. Can also be served on the rocks in a rocks glass for a longer drinking experience."}'::jsonb, 'Shot glass (or Rocks glass)', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_AMARETTO"}', '{"creamy","sweet","coffee","nutty"}', 15, 180, 'easy', 30, '{"milk","tree_nuts"}', '{"lactose","alcohol","caffeine","nut_allergy"}', '{"vegetarian","pescatarian","gluten_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"party","celebration","casual"}', false, false, '{"screaming-orgasm","multiple-orgasm","slow-comfortable-screw"}', 'Contains nut liqueur - check for allergies. Name may require discretion with certain clientele. Can be made as a cocktail over ice for slower consumption. Very popular party shot. Some recipes add vodka (becomes Screaming Orgasm).', 'low', 75, 'https://www.liquor.com/recipes/orgasm/', 'Classic 1980s party shooter with provocative name.', 1)
