-- 188. Pegu Club


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('fff6731c-e791-41b5-a2bc-e05949988138', 'pegu-club', 'm3n4o5p6q7r8s9t0u1v2w3x4y5z6a7b8c9d0e1f2', '{"en":"Pegu Club"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","gin","citrus","colonial","exotic"}', '{"en":"An exotic gin sour from British Colonial Burma. The Pegu Club combines gin with orange curaçao, lime juice, and aromatic bitters for a sophisticated, citrus-forward cocktail with a touch of tropical intrigue. Named after the legendary gentlemen''s club in Rangoon, it''s a taste of colonial elegance."}'::jsonb, '{"created_year":"1920","origin":{"city":"Rangoon (Yangon)","bar":"Pegu Club","country":"Burma (Myanmar)"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Pegu Club cocktail was created at the legendary Pegu Club, a colonial-era gentlemen''s club in Rangoon (now Yangon), Burma (now Myanmar), established in the 1880s. The club, named after the nearby Pegu River, was the social center for British colonial officers and elite expatriates. The cocktail first appeared in print in Harry MacElhone''s \"Harry''s ABC of Mixing Cocktails\" (1922) and later in Harry Craddock''s \"The Savoy Cocktail Book\" (1930). The drink embodies the exotic sophistication of British colonial bar culture - a gin sour elevated with orange liqueur and bitters. After World War II, the Pegu Club fell into decline, and the cocktail became obscure. It was revived in the 2000s when Audrey Saunders opened a New York cocktail bar named \"Pegu Club\" in its honor, reintroducing this forgotten classic to modern drinkers."},"named_after":{"en":"Named after the Pegu Club, a famous colonial-era gentlemen''s club in Rangoon, Burma, which was named after the Pegu River."}}'::jsonb, '{"profile":["citrus","bitter","sophisticated"],"description":{"en":"Crisp, citrusy, and elegantly bitter. The Pegu Club balances gin''s botanicals with bright lime acidity, orange curaçao''s sweetness, and the aromatic complexity of bitters. It''s a more sophisticated, exotic take on the classic gin sour."},"first_impression":{"en":"Sharp lime acidity with orange sweetness, followed by gin botanicals and aromatic bitters"},"finish":{"en":"Clean, crisp finish with lingering lime, orange, and subtle bitter spice"},"balance":{"en":"Perfectly balanced between tart lime, sweet orange, and aromatic bitters - sophisticated and complex"}}'::jsonb, '{"best_time":["evening","late_afternoon"],"occasions":["aperitivo","sophisticated","date_night","colonial_theme"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Pairs beautifully with Southeast Asian cuisine, Indian dishes, curry, grilled seafood, and spicy appetizers. Also excellent with British colonial fare and afternoon tea snacks."},"ideal_for":{"en":"Perfect for gin lovers who appreciate citrus-forward sours with complexity. Ideal for cocktail historians and those who enjoy exotic, colonial-era drinks. A sophisticated choice for tropical or Asian-themed gatherings."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":60,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_ORANGE_CURACAO","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Orange curaçao","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_ANGOSTURA_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Angostura bitters","notes":null},{"ingredient_id":"ING_ORANGE_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Orange bitters","notes":null}]'::jsonb, 'shake', '{"en":"Pour all ingredients into a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled cocktail glass. Garnish with a lime wheel or twist."}'::jsonb, 'Cocktail glass (Coupe)', '{"en":"Lime wheel or twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"citrus","bitter","sophisticated"}', 24, 165, 'medium', 60, '{}', '{"alcohol"}', '{"vegetarian","vegan","pescatarian","gluten_free","nut_free","dairy_free"}', 0, '{"vegetarian","vegan","gluten-free"}', '{"spring","summer","autumn"}', '{"aperitivo","sophisticated","date_night"}', false, false, '{"modern-pegu-club","rangoon-ruby"}', 'Always use fresh lime juice. Both Angostura and orange bitters are essential - don''t skip either. Pierre Ferrand Dry Curaçao or Cointreau work best. Shake hard to properly chill and dilute. The classic ratio is roughly 8:3:3 (gin:curaçao:lime).', 'mid', 55, 'https://www.diffordsguide.com/cocktails/recipe/1617/pegu-club', 'Classic recipe from The Savoy Cocktail Book (1930) and Harry''s ABC of Mixing Cocktails (1922).', 1)
