INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('825db886-95c8-450f-99d1-7efd73435ce9', 'pickleback', 'pickleback_shot_famous_whiskey_pickle_brine_chaser', '{"en":"Pickleback"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","whiskey","savory","pickle","chaser","brooklyn"}', '{"en":"A shot of whiskey followed immediately by a shot of pickle brine as a chaser. This unconventional combination became a Brooklyn bar phenomenon, with the pickle juice cutting through the whiskey burn and leaving a surprisingly clean finish."}'::jsonb, '{"created_year":"2006","origin":{"city":"Brooklyn","bar":"Bushwick Country Club","country":"USA"},"creator":{"name":"Reggie Cunningham","profession":"bartender"},"story":{"en":"The Pickleback was invented in 2006 by bartender Reggie Cunningham at the Bushwick Country Club in Brooklyn, New York. The story goes that a regular customer requested pickle juice to chase his whiskey shot, and the combination was so surprisingly effective that it caught on. The drink spread rapidly through Brooklyn''s bar scene and eventually became a nationwide phenomenon, spawning variations with different pickle brines and spirits."},"named_after":{"en":"Named \"Pickleback\" because the pickle brine acts as a \"back\" (chaser) for the whiskey shot."}}'::jsonb, '{"profile":["savory","salty","sour","briny"],"description":{"en":"The whiskey hits first with its characteristic burn and warmth. The pickle brine chaser immediately follows, cutting through the alcohol with tangy, salty, vinegar-forward flavor. The combination neutralizes the whiskey burn surprisingly well, leaving a clean, refreshing finish."},"first_impression":{"en":"Whiskey burn followed by tangy, salty pickle brine"},"finish":{"en":"Surprisingly clean and refreshing with lingering pickle flavor"},"balance":{"en":"The pickle brine perfectly neutralizes whiskey burn - oddly satisfying"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","casual","bar_hopping","dive_bar"],"seasons":["all_seasons"],"food_pairings":{"en":"Excellent with bar snacks, fried foods, burgers, and deli sandwiches. The savory nature complements salty, fatty foods perfectly."},"ideal_for":{"en":"Perfect for whiskey drinkers who want an easier chaser than beer or water. Great for those who love pickles and savory drinks. A fun novelty that actually works remarkably well."}}'::jsonb, '[{"ingredient_id":"ING_WHISKEY","quantity":{"amount":45,"unit":"ml"},"display_name":"Whiskey (bourbon or rye)","notes":null},{"ingredient_id":"ING_PICKLE_BRINE","quantity":{"amount":45,"unit":"ml"},"display_name":"Pickle Brine (dill pickle juice)","notes":null}]'::jsonb, 'pour', '{"en":"Pour whiskey into one shot glass and pickle brine into another. Drink the whiskey shot first, immediately followed by the pickle brine chaser. Do not pause between shots."}'::jsonb, 'Shot glass (two required)', '{"en":"Pickle spear (optional, on the side)"}'::jsonb, 'none', 'shot', '{"ING_WHISKEY"}', '{"savory","salty","sour","briny"}', 20, 100, 'easy', 20, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"all-seasons"}', '{"party","casual","bar-hopping"}', false, false, '{"tequila-pickleback","mezcal-pickleback","vodka-pickleback","kimchi-back"}', 'Use dill pickle brine from good quality pickles - avoid sweet pickle juice. Some customers prefer spicy pickle brine. Serve both shots at the same time. Can substitute with other whiskeys or even tequila/mezcal. Very polarizing - some love it, others hate the idea.', 'low', 70, 'https://en.wikipedia.org/wiki/Pickleback', 'Brooklyn bar invention from 2006. Whiskey shot with pickle brine chaser.', 1)
