-- 193. Port Light


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('33e26646-94a4-4594-a2cd-28be48b9fa53', 'port-light', 'port_light_famous_tiki_tropical_cocktail', '{"en":"Port Light"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","bourbon","unique"}', '{"en":"The Port Light is an innovative tiki cocktail created by Don the Beachcomber that uniquely combines rum with bourbon whiskey, passion fruit, honey, and citrus. This unusual spirit combination creates surprising depth and complexity rarely found in tropical drinks."}'::jsonb, '{"created_year":"1941","origin":{"city":"Hollywood","bar":"Don the Beachcomber","country":"USA"},"creator":{"name":"Ernest Raymond Beaumont Gantt (Donn Beach)","profession":"bartender"},"story":{"en":"The Port Light was created around 1941 by Don the Beachcomber during the early days of World War II. The name \"Port Light\" refers to the red navigation light on the port (left) side of ships - a fitting naval reference for the wartime era. What makes this drink revolutionary is Don''s bold decision to combine bourbon whiskey with rum - at a time when mixing different base spirits in cocktails was considered unconventional. The bourbon adds oak, vanilla, and caramel notes that complement the tropical ingredients in unexpected ways. The cocktail also features passion fruit and honey, creating a complex interplay between American whiskey tradition and tropical tiki culture. The recipe remained encoded in Don''s secret notation system until decoded by Jeff \"Beachbum\" Berry decades later."},"named_after":{"en":"Named after the red port light on the left side of ships, a maritime navigation reference fitting the wartime era."}}'::jsonb, '{"profile":["complex","honeyed","tropical"],"description":{"en":"Remarkably complex with bourbon''s oak and vanilla notes playing against tropical passion fruit and honey. The Port Light creates an unexpected harmony between whiskey warmth and rum''s tropical character, unified by honeyed sweetness and bright citrus. A sophisticated crossover between tiki and classic American cocktails."},"first_impression":{"en":"Bourbon oak and vanilla hit first, quickly joined by tropical passion fruit sweetness"},"finish":{"en":"Long, warming finish with bourbon spice, honey sweetness, and lingering tropical notes"},"balance":{"en":"Brilliantly balanced between American whiskey tradition and tropical tiki innovation"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["tiki_bar","whiskey_lovers","unique_experience"],"seasons":["autumn","winter"],"food_pairings":{"en":"Perfect with smoked meats, bourbon-glazed ribs, tropical BBQ, or grilled pineapple. Also pairs well with rich desserts and caramelized dishes that complement both bourbon and tropical flavors."},"ideal_for":{"en":"Perfect for whiskey lovers curious about tiki culture and adventurous drinkers seeking unique flavor combinations. Ideal for those who find traditional tiki drinks too sweet or one-dimensional. A bridge between bourbon and tiki worlds."}}'::jsonb, '[{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":45,"unit":"ml"},"display_name":"Dark rum (Jamaican)","notes":null},{"ingredient_id":"ING_BOURBON","quantity":{"amount":30,"unit":"ml"},"display_name":"Bourbon whiskey","notes":null},{"ingredient_id":"ING_PASSION_FRUIT_SYRUP","quantity":{"amount":30,"unit":"ml"},"display_name":"Passion fruit syrup","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_HONEY_SYRUP","quantity":{"amount":15,"unit":"ml"},"display_name":"Honey syrup","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a double old-fashioned glass filled with crushed ice. Garnish with lime wheel and edible flower."}'::jsonb, 'Double old-fashioned', '{"en":"Lime wheel, edible flower, mint sprig"}'::jsonb, 'crushed', 'on_the_rocks', '{"ING_RUM_DARK","ING_BOURBON"}', '{"complex","honeyed","tropical"}', 18, 250, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 1, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"tiki_bar","whiskey_lovers","unique_experience"}', false, false, '{"don-the-beachcomber-port-light"}', 'Created by Don the Beachcomber circa 1941. Unique rum-bourbon combination - explain this unusual pairing to guests. Use high-quality bourbon for best results. Can substitute passion fruit purée for syrup. Honey syrup is 1:1 honey to water. Great bridge drink for whiskey lovers exploring tiki.', 'premium', 64, 'https://beachbumberry.com/port-light.html', 'Original Don the Beachcomber recipe circa 1941, decoded by Jeff "Beachbum" Berry in Sippin'' Safari.', 1)
