-- 195. Red Headed Slut


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('113a493f-8059-4f6b-be1b-4b873f12e69b', 'red-headed-slut', 'red_headed_slut_shot_famous_peach_cranberry', '{"en":"Red Headed Slut"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","fruity","sweet","party"}', '{"en":"A sweet and fruity shooter combining peach schnapps, Jägermeister, and cranberry juice. Despite its provocative name, this popular party shot delivers a surprisingly smooth and sweet flavor that masks its alcoholic strength."}'::jsonb, '{"created_year":"1980s","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"story":{"en":"The Red Headed Slut emerged in American bars during the 1980s party scene. Its exact origin is disputed, but the shot gained widespread popularity in college towns and nightclubs. The name, while controversial, became part of its identity and contributed to its notoriety as a fun, party-oriented drink."},"named_after":{"en":"The name is a provocative reference to the drink''s reddish color from cranberry juice."}}'::jsonb, '{"profile":["sweet","fruity","herbal"],"description":{"en":"Surprisingly sweet with peach and cranberry flavors dominating. The Jägermeister adds herbal complexity without overwhelming bitterness. Smooth and easy to drink despite the strong alcohol content."},"first_impression":{"en":"Sweet peach and cranberry with subtle herbal notes"},"finish":{"en":"Smooth finish with lingering peach sweetness and herbal warmth"},"balance":{"en":"Sweet and fruity flavors balance the herbal intensity of Jägermeister"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","nightlife","celebration","bachelor_bachelorette"],"seasons":["all_seasons"],"food_pairings":{"en":"Best enjoyed as a party shot without food pairings. Can be followed by light snacks or appetizers."},"ideal_for":{"en":"Perfect for party-goers who enjoy sweet, easy-drinking shots. A popular choice for those who want to mask the taste of strong alcohol."}}'::jsonb, '[{"ingredient_id":"ING_JAGERMEISTER","quantity":{"amount":15,"unit":"ml"},"display_name":"Jägermeister","notes":null},{"ingredient_id":"ING_PEACH_SCHNAPPS","quantity":{"amount":15,"unit":"ml"},"display_name":"Peach Schnapps","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Cranberry Juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake well until chilled. Strain into a shot glass and serve immediately."}'::jsonb, 'Shot glass', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_JAGERMEISTER","ING_PEACH_SCHNAPPS"}', '{"sweet","fruity","herbal"}', 24, 100, 'easy', 40, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"all-seasons"}', '{"party","nightlife","celebration"}', false, false, '{"ginger-headed-slut","blonde-headed-slut","brunette-slut"}', 'Despite the provocative name, this is a popular and requested shot. Some venues may use alternative names like "Ginger Shot" or "Peachy Keen" to avoid controversy. Shake well to ensure proper mixing.', 'low', 70, 'https://www.liquor.com/recipes/red-headed-slut/', 'Classic party shot from the 1980s bar scene.', 1)
