-- 199. Rum Runner


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('f302fa97-835d-4d1b-be5a-f5503deb2747', 'rum-runner', 'rum_runner_famous_tiki_tropical_cocktail', '{"en":"Rum Runner"}'::jsonb, 'famous', NULL, '{"tiki","tropical","famous","rum","fruity","frozen"}', '{"en":"The Rum Runner is a frozen tropical cocktail born in the Florida Keys, combining light and dark rum with banana liqueur, blackberry liqueur, and tropical fruit juices. This fruity, potent blend is a staple of Key West tiki culture."}'::jsonb, '{"created_year":"1972","origin":{"city":"Key West","bar":"Holiday Isle Tiki Bar","country":"USA"},"creator":{"name":"John \"Tiki John\" Ebert","profession":"bartender"},"story":{"en":"The Rum Runner was created in 1972 by bartender John \"Tiki John\" Ebert at the Holiday Isle Tiki Bar in Islamorada, Florida Keys. The story goes that Ebert had a surplus of rum and liqueurs that he needed to use up, so he experimented with combining various ingredients. The result was this potent frozen concoction that became an instant hit with tourists and locals alike. The name \"Rum Runner\" pays homage to the prohibition-era smugglers who ran rum from the Caribbean to the Florida Keys. The drink perfectly captures the laid-back, tropical spirit of the Keys and remains a signature cocktail of the region."},"named_after":{"en":"Named after prohibition-era rum smugglers who operated in the Caribbean and Florida Keys."}}'::jsonb, '{"profile":["fruity","sweet","tropical"],"description":{"en":"Intensely fruity and tropical with layers of banana, blackberry, and citrus flavors. The Rum Runner is sweet and smooth with a boozy backbone, offering a complex fruit profile that tastes like a Caribbean vacation in a glass."},"first_impression":{"en":"Sweet berry and banana flavors dominate upfront, followed by tropical fruit complexity"},"finish":{"en":"Smooth, fruity finish with lingering banana and berry notes and subtle rum warmth"},"balance":{"en":"Sweet and fruity with multiple liqueurs creating complex flavor layers, rum provides subtle strength"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["beach","pool_party","vacation","summer_gathering"],"seasons":["spring","summer"],"food_pairings":{"en":"Perfect with Florida stone crab, key lime pie, conch fritters, grilled mahi-mahi, or tropical fruit salads. Also pairs well with spicy Caribbean jerk dishes."},"ideal_for":{"en":"Perfect for beach vacationers and frozen drink enthusiasts. Ideal for anyone seeking a potent, fruity escape to the Florida Keys. Great for poolside relaxation and tropical celebrations."}}'::jsonb, '[{"ingredient_id":"ING_RUM_WHITE","quantity":{"amount":30,"unit":"ml"},"display_name":"White rum","notes":null},{"ingredient_id":"ING_RUM_DARK","quantity":{"amount":30,"unit":"ml"},"display_name":"Dark rum","notes":null},{"ingredient_id":"ING_CREME_DE_BANANE","quantity":{"amount":15,"unit":"ml"},"display_name":"Banana liqueur","notes":null},{"ingredient_id":"ING_BLACKBERRY_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Blackberry liqueur","notes":null},{"ingredient_id":"ING_PINEAPPLE_JUICE","quantity":{"amount":60,"unit":"ml"},"display_name":"Pineapple juice","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":15,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_GRENADINE","quantity":{"amount":7.5,"unit":"ml"},"display_name":"Grenadine","notes":null}]'::jsonb, 'blend', '{"en":"Add all ingredients to a blender with 1 cup of crushed ice. Blend until smooth. Pour into a hurricane glass. Garnish with orange slice and maraschino cherry."}'::jsonb, 'Hurricane glass', '{"en":"Orange slice, maraschino cherry, pineapple wedge"}'::jsonb, 'blended', 'frozen', '{"ING_RUM_WHITE","ING_RUM_DARK"}', '{"fruity","sweet","tropical"}', 13, 310, 'easy', 120, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"beach","pool_party","vacation","summer_gathering"}', false, false, '{"key-west-rum-runner","florida-keys-runner"}', 'Created at Holiday Isle Tiki Bar in 1972. Can use crème de cassis if blackberry liqueur unavailable. Some recipes add orange juice. Blended version is traditional, but can be shaken and served over ice for a non-frozen variant.', 'mid', 78, 'https://www.holidayisle.com/rum-runner', 'Original recipe from Holiday Isle Tiki Bar, created by Tiki John Ebert in 1972. Florida Keys cocktail archives.', 1)
