-- 201. Satan's Whiskers


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('0fc963b4-33e7-452f-aff7-c6e754a25dcd', 'satans-whiskers', 'b4c5d6e7f8a9b0c1d2e3f4a5b6c7d8e9f0a1b2c3', '{"en":"Satan''s Whiskers"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","gin","complex","citrus"}', '{"en":"A complex cocktail featuring gin, sweet and dry vermouth, orange juice, Grand Marnier, and orange bitters. Satan''s Whiskers is a sophisticated blend of citrus, botanicals, and dual vermouths creating a devilishly delicious drink."}'::jsonb, '{"created_year":"1930","origin":{"city":"London","bar":"Unknown","country":"UK"},"creator":{"name":"Harry Craddock","profession":"bartender"},"story":{"en":"Satan''s Whiskers appeared in Harry Craddock''s legendary \"The Savoy Cocktail Book\" (1930). The drink comes in two versions: \"Straight\" (using Grand Marnier) and \"Curled\" (using orange curaçao). The provocative name reflects the drink''s devilishly complex character and potent nature. This cocktail represents the sophisticated complexity of 1930s London cocktail culture."},"named_after":{"en":"The provocative name \"Satan''s Whiskers\" refers to the drink''s devilishly complex and potent character, suggesting something dangerously delicious."}}'::jsonb, '{"profile":["complex","citrus","botanical"],"description":{"en":"Intensely complex with fresh orange juice balanced by dual vermouths, Grand Marnier''s cognac-orange richness, botanical gin, and aromatic orange bitters. Sophisticated and layered with every sip revealing new nuances."},"first_impression":{"en":"Fresh orange hits first with botanical gin and vermouth complexity following"},"finish":{"en":"Long, sophisticated finish with lingering orange, cognac warmth, and botanical notes"},"balance":{"en":"Masterfully balanced complexity - citrus-forward yet spirit-strong with perfect harmony"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","aperitivo","date_night","special_occasion"],"seasons":["spring","summer","autumn"],"food_pairings":{"en":"Excellent with brunch dishes, smoked salmon, citrus salads, or as a sophisticated aperitif. Pairs well with French and Mediterranean cuisines."},"ideal_for":{"en":"Perfect for gin lovers who appreciate complex, citrus-forward cocktails. Ideal for those seeking sophisticated alternatives to the Bronx or similar orange juice cocktails."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_SWEET_VERMOUTH","quantity":{"amount":15,"unit":"ml"},"display_name":"Sweet vermouth","notes":null},{"ingredient_id":"ING_DRY_VERMOUTH","quantity":{"amount":15,"unit":"ml"},"display_name":"Dry vermouth","notes":null},{"ingredient_id":"ING_GRAND_MARNIER","quantity":{"amount":15,"unit":"ml"},"display_name":"Grand Marnier","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":22.5,"unit":"ml"},"display_name":"Fresh orange juice","notes":null},{"ingredient_id":"ING_ORANGE_BITTERS","quantity":{"amount":1,"unit":"dash"},"display_name":"Orange bitters","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously until well-chilled. Strain into a chilled coupe or cocktail glass. Garnish with an orange twist, expressing the oils over the drink."}'::jsonb, 'Coupe glass', '{"en":"Orange twist"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"complex","citrus","botanical"}', 20, 180, 'medium', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer","autumn"}', '{"brunch","aperitivo","date_night","special_occasion"}', false, false, '{"satans-whiskers-curled"}', 'This is the "Straight" version with Grand Marnier. "Curled" version uses orange curaçao instead. Use fresh orange juice only. Quality vermouths essential. Shake hard to properly integrate all ingredients. Complex but worth the effort.', 'mid', 50, 'https://www.diffordsguide.com/cocktails/recipe/1508/satans-whiskers-straight', 'Harry Craddock''s "The Savoy Cocktail Book" (1930). Classic complex gin cocktail.', 1)
