-- 205. Screaming Orgasm


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('97845c63-facf-4587-bad4-e08286175a9c', 'screaming-orgasm', 'screaming_orgasm_vodka_amaretto_kahlua_baileys_cream', '{"en":"Screaming Orgasm"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","shooter","sweet","coffee","nutty","vodka-based"}', '{"en":"The boozy big brother of the Orgasm, adding vodka to the mix of amaretto, coffee liqueur, Irish cream, and cream. This infamous shooter delivers an even more intense explosion of sweet, creamy flavors with an extra alcoholic kick."}'::jsonb, '{"created_year":"1980s","origin":{"country":"USA"},"story":{"en":"The Screaming Orgasm emerged shortly after the original Orgasm shot in the 1980s. Bartenders created this variation by adding vodka to increase the alcohol content while maintaining the same sweet, creamy flavor profile. The name escalation from \"Orgasm\" to \"Screaming Orgasm\" suggested a more intense experience. This shot became part of the notorious family of provocatively-named drinks that dominated American bar culture in the late 1980s and early 1990s, particularly popular at bachelor/bachelorette parties and nightclubs."},"named_after":{"en":"The provocative name suggests an even more intense, pleasurable experience than the original Orgasm shot, thanks to the added vodka."}}'::jsonb, '{"profile":["creamy","sweet","coffee","nutty","boozy"],"description":{"en":"Intensely sweet and creamy with harmonious notes of almond, coffee, and chocolate, plus a noticeable vodka kick. The vodka adds strength without altering the dessert-like flavor profile, creating a more potent version of the original Orgasm."},"first_impression":{"en":"Sweet cream explosion with immediate almond, coffee, and vodka punch"},"finish":{"en":"Lingering sweetness with stronger alcohol warmth than the original"},"balance":{"en":"Very sweet and indulgent with more alcoholic strength - designed for party intensity"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","bachelor_party","nightclub"],"seasons":["all_seasons"],"food_pairings":{"en":"Typically consumed as a standalone party shot. Best enjoyed between other drinks or as a liquid dessert."},"ideal_for":{"en":"Perfect for those who want a stronger version of the Orgasm shot. Ideal for high-energy parties, celebrations, and guests seeking an indulgent but potent shot. Popular at bachelor/bachelorette parties."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":15,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_AMARETTO","quantity":{"amount":15,"unit":"ml"},"display_name":"Amaretto","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":15,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null},{"ingredient_id":"ING_IRISH_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Irish cream (Baileys)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 10 seconds until well-chilled. Strain into a shot glass or double shot glass. Shoot in one gulp for full impact. Can also be served on the rocks in a rocks glass for a cocktail version."}'::jsonb, 'Shot glass (or Double shot glass)', '{"en":"None"}'::jsonb, 'none', 'shot', '{"ING_VODKA"}', '{"creamy","sweet","coffee","nutty","boozy"}', 20, 210, 'easy', 30, '{"milk","tree_nuts"}', '{"lactose","alcohol","caffeine","nut_allergy"}', '{"vegetarian","pescatarian","gluten_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"party","celebration","nightclub"}', false, false, '{"orgasm","multiple-orgasm","screaming-multiple-orgasm"}', 'Contains nut liqueur - check for allergies. Stronger than regular Orgasm due to vodka. Name may require discretion. Very popular at bachelor/bachelorette parties. Can be made as a cocktail over ice. Some recipes use equal parts of all five ingredients.', 'low', 70, 'https://www.diffords guide.com/cocktails/recipe/2152/screaming-orgasm', 'Stronger variation of the Orgasm shot, popular in 1980s-90s bar culture.', 1)
