-- 206. Screwdriver


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('be73e0b9-8684-416e-9f67-8328445f585a', 'screwdriver', 'a0b1c2d3e4f5a6b7c8d9e0f1a2b3c4d5e6f7a8b9', '{"en":"Screwdriver"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","classic","vodka","orange","brunch"}', '{"en":"One of the most iconic and simple cocktails ever created, combining vodka with fresh orange juice. The Screwdriver is the quintessential brunch drink and the foundation for countless variations."}'::jsonb, '{"created_year":"1950","origin":{"city":"Persian Gulf","country":"Various claims"},"story":{"en":"The Screwdriver''s origin story has several versions, but the most popular claims it was invented by American oil workers in the Persian Gulf in the 1950s. Unable to find proper bar tools, they stirred their drinks with screwdrivers, hence the name. Another theory suggests it was invented by American engineers working abroad who used the tool to mix their drinks. The cocktail gained massive popularity in the 1960s and 1970s with the rise of vodka in America, becoming the go-to brunch cocktail."},"named_after":{"en":"Named after the tool (screwdriver) allegedly used by oil workers to stir the drink when proper bar tools were unavailable."}}'::jsonb, '{"profile":["citrus","sweet","refreshing"],"description":{"en":"Bright, sweet, and incredibly easy-drinking. The natural sweetness of fresh orange juice perfectly complements the neutral vodka base, creating a smooth, fruity cocktail that''s neither too strong nor too sweet."},"first_impression":{"en":"Fresh orange juice upfront with a subtle vodka warmth"},"finish":{"en":"Clean, sweet citrus finish with minimal alcohol burn"},"balance":{"en":"The vodka provides structure without overpowering the natural orange sweetness - perfect harmony"}}'::jsonb, '{"best_time":["morning","brunch","afternoon"],"occasions":["brunch","casual","breakfast","poolside"],"seasons":["all-year"],"food_pairings":{"en":"Classic brunch companion - pairs perfectly with eggs benedict, pancakes, waffles, French toast, bacon, and breakfast pastries. Also works well with light salads and sandwiches."},"ideal_for":{"en":"Perfect for brunch lovers and anyone who wants an easy-drinking, crowd-pleasing cocktail. Great for those new to cocktails or anyone seeking a simple, refreshing drink without complexity."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":50,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_ORANGE_JUICE","quantity":{"amount":100,"unit":"ml"},"display_name":"Fresh orange juice","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour vodka over ice, then top with fresh orange juice. Stir gently to combine. Garnish with an orange slice."}'::jsonb, 'Highball glass', '{"en":"Orange slice"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"citrus","sweet","refreshing"}', 10, 165, 'easy', 30, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"all-year"}', '{"brunch","casual","breakfast"}', false, false, '{"harvey-wallbanger","fuzzy-navel","sloe-screw"}', 'Always use freshly squeezed orange juice, never from concentrate. Quality of OJ makes or breaks this drink. Standard ratio is 1:2 (vodka:OJ) but can adjust to preference.', 'low', 95, 'https://www.diffordsguide.com/cocktails/recipe/1236/screwdriver', 'Classic American cocktail, documented in numerous sources.', 1)
