-- 210. Southside


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('39738cee-11d9-4b67-9248-6c18ac5e080f', 'southside', 'd4e5f6a7b8c9d0e1f2a3b4c5d6e7f8a9b0c1d2e3', '{"en":"Southside"}'::jsonb, 'famous', NULL, '{"classic","vintage","famous","gin","refreshing","minty"}', '{"en":"A refreshing gin cocktail with fresh mint, lime, and simple syrup. Often called the \"gin mojito,\" the Southside is a classic Prohibition-era drink that balances botanical gin with bright citrus and cooling mint."}'::jsonb, '{"created_year":"1920","origin":{"city":"Chicago","bar":"21 Club","country":"USA"},"creator":{"name":"Unknown","profession":"bartender"},"story":{"en":"The Southside emerged during Prohibition in the 1920s, with disputed origins between New York''s 21 Club and Chicago''s South Side gangsters. Some believe it was created to mask the harsh taste of bathtub gin with fresh mint and citrus. The drink became a sophisticated staple at speakeasies and has remained popular ever since."},"named_after":{"en":"Likely named after the South Side of Chicago, where gangsters allegedly enjoyed this drink during Prohibition."}}'::jsonb, '{"profile":["refreshing","citrus","herbal"],"description":{"en":"Bright and refreshing with a perfect balance of tart lime, aromatic gin botanicals, and cooling fresh mint. The simple syrup rounds out the edges for a smooth, elegant finish."},"first_impression":{"en":"Fresh mint aroma hits first, followed by bright lime and botanical gin notes"},"finish":{"en":"Clean, refreshing finish with lingering mint and subtle juniper"},"balance":{"en":"Perfectly balanced between tart, sweet, and herbal - refreshing without being cloying"}}'::jsonb, '{"best_time":["afternoon","evening"],"occasions":["brunch","garden_party","date_night","aperitivo"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with light seafood, fresh salads, goat cheese, or as a pre-dinner aperitif. Pairs wonderfully with Mediterranean dishes and fresh herbs."},"ideal_for":{"en":"Perfect for gin lovers who enjoy fresh, herbaceous cocktails. An excellent choice for warm weather or anyone seeking a sophisticated alternative to the mojito."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":60,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_LIME_JUICE","quantity":{"amount":30,"unit":"ml"},"display_name":"Fresh lime juice","notes":null},{"ingredient_id":"ING_SIMPLE_SYRUP","quantity":{"amount":20,"unit":"ml"},"display_name":"Simple syrup","notes":null},{"ingredient_id":"ING_MINT_LEAVES","quantity":{"amount":8,"unit":"leaves"},"display_name":"Fresh mint leaves","notes":null}]'::jsonb, 'shake', '{"en":"Add mint leaves, lime juice, and simple syrup to a cocktail shaker and gently muddle. Add gin and ice, then shake vigorously until well-chilled. Double strain into a chilled coupe glass. Garnish with a fresh mint sprig."}'::jsonb, 'Coupe glass', '{"en":"Fresh mint sprig"}'::jsonb, 'none', 'up', '{"ING_GIN"}', '{"refreshing","citrus","herbal"}', 18, 160, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegetarian","vegan","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegetarian","vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"brunch","garden_party","date_night","aperitivo"}', false, false, '{"southside-royale","southside-fizz"}', 'Muddle mint gently to avoid bitterness. Double strain to remove mint particles. Use fresh lime juice only - never bottled. Can be served fizz-style by topping with champagne.', 'mid', 70, 'https://punchdrink.com/articles/southside-cocktail-recipe-gin-mint-lime/', 'Classic Prohibition-era recipe. Historical information from cocktail history sources.', 1)
