-- 214. Tiramisu Martini


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('a76fe2c0-2376-43c3-bcac-27340c8dff70', 'tiramisu-martini', 'tiramisu_martini_vodka_kahlua_baileys_cream', '{"en":"Tiramisu Martini"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","coffee","chocolate","sweet","vodka-based","martini-style"}', '{"en":"A liquid version of the beloved Italian dessert, combining vodka, coffee liqueur, Irish cream, and cream to recreate the flavors of tiramisu in cocktail form. This indulgent martini captures the essence of coffee-soaked ladyfingers and mascarpone cream."}'::jsonb, '{"created_year":"2000s","origin":{"country":"USA"},"story":{"en":"The Tiramisu Martini emerged in the early 2000s as bartenders sought to create cocktail versions of popular desserts. Inspired by the classic Italian tiramisu dessert, mixologists combined coffee liqueur, Irish cream, and vodka to mimic the coffee, cream, and mascarpone flavors of the original dish. The cocktail became popular in upscale Italian restaurants and cocktail bars, offering a sophisticated way to enjoy tiramisu flavors without the fork. It represents the trend of dessert-inspired cocktails that dominated the 2000s."},"named_after":{"en":"Named after the classic Italian dessert \"Tiramisu,\" which means \"pick me up\" in Italian, referring to its coffee content."}}'::jsonb, '{"profile":["creamy","sweet","coffee","chocolate"],"description":{"en":"A perfect recreation of tiramisu flavors: rich coffee notes balanced with sweet cream and hints of chocolate. The Irish cream adds a mascarpone-like richness while the coffee liqueur provides the espresso character. Sweet, indulgent, and remarkably similar to the dessert."},"first_impression":{"en":"Coffee and cream hit first, immediately reminiscent of tiramisu"},"finish":{"en":"Long, sweet finish with coffee and cocoa notes, just like the dessert"},"balance":{"en":"Well-balanced for a dessert cocktail, with coffee and cream in harmony"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["dessert","date_night","italian_dinner","celebration"],"seasons":["all_seasons"],"food_pairings":{"en":"Perfect as a dessert replacement, especially after Italian meals. Can accompany biscotti, chocolate desserts, or coffee-flavored sweets. Also pairs with cannoli and panna cotta."},"ideal_for":{"en":"Perfect for tiramisu lovers who want a liquid version of their favorite dessert. Ideal for Italian restaurant endings, coffee enthusiasts, and anyone seeking an indulgent after-dinner cocktail with familiar flavors."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":30,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":30,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null},{"ingredient_id":"ING_IRISH_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Irish cream (Baileys)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":15,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Rim a chilled martini glass with cocoa powder. Add all ingredients to a cocktail shaker filled with ice. Shake vigorously for 15 seconds until well-chilled and frothy. Double strain into the prepared martini glass. Dust the top with cocoa powder to mimic the traditional tiramisu presentation."}'::jsonb, 'Martini glass', '{"en":"Cocoa powder dusting, chocolate shavings, or ladyfinger cookie"}'::jsonb, 'none', 'up', '{"ING_VODKA"}', '{"creamy","sweet","coffee","chocolate"}', 17, 340, 'easy', 90, '{"milk"}', '{"lactose","alcohol","caffeine"}', '{"vegetarian","pescatarian","gluten_free","nut_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"dessert","date-night","celebration"}', false, false, '{"espresso-tiramisu-martini","frozen-tiramisu-martini","tiramisu-white-russian"}', 'The cocoa powder rim and dusting is essential for authentic tiramisu look. Some recipes use amaretto instead of vodka. Can add a shot of espresso for more coffee intensity. Popular in Italian restaurants as a liquid dessert option. High calorie content.', 'mid', 75, 'https://www.allrecipes.com/recipe/228654/tiramisu-martini/', 'Dessert-inspired cocktail popular in the 2000s.', 1)
