-- 215. Toasted Almond


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('d07ac9b8-33e8-4a35-bd6d-60f3ce0eeb47', 'toasted-almond', 'toasted_almond_amaretto_kahlua_cream', '{"en":"Toasted Almond"}'::jsonb, 'famous', NULL, '{"creamy","dessert","famous","coffee","nutty","sweet"}', '{"en":"A smooth, nutty dessert cocktail combining amaretto, coffee liqueur, and cream. The Toasted Almond offers a perfect balance of almond and coffee flavors wrapped in silky cream, creating an irresistible after-dinner treat."}'::jsonb, '{"created_year":"1970s","origin":{"country":"USA"},"story":{"en":"The Toasted Almond emerged in the 1970s during the golden age of creamy cocktails in America. It''s often considered a variation of the Sombrero (Kahlúa and cream) or a cousin to the Orgasm family of shooters. The drink became popular in bars and restaurants as an accessible, crowd-pleasing dessert cocktail that showcased the then-trendy amaretto liqueur. Its simple recipe and delicious flavor profile have kept it popular in casual dining establishments."},"named_after":{"en":"Named for the \"toasted\" almond flavor achieved by combining amaretto with coffee liqueur, evoking the taste of roasted almonds."}}'::jsonb, '{"profile":["creamy","sweet","nutty","coffee"],"description":{"en":"Rich and creamy with pronounced almond and coffee notes. The amaretto provides sweet nuttiness while the coffee liqueur adds depth and a subtle roasted quality, all softened by cream into a smooth, dessert-like drink."},"first_impression":{"en":"Sweet almond cream with immediate coffee undertones"},"finish":{"en":"Smooth, lingering almond and coffee sweetness"},"balance":{"en":"Well-balanced between nutty and coffee flavors, though decidedly sweet and dessert-focused"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["digestivo","casual","dessert","nightcap"],"seasons":["all_seasons"],"food_pairings":{"en":"Perfect with almond-based desserts, biscotti, tiramisu, or chocolate cake. Also pairs well with vanilla ice cream and amaretti cookies."},"ideal_for":{"en":"Perfect for those who enjoy nutty, coffee-flavored dessert drinks. Ideal for casual dining settings and anyone seeking a simple but delicious after-dinner cocktail. Great alternative to Irish Coffee or White Russian."}}'::jsonb, '[{"ingredient_id":"ING_AMARETTO","quantity":{"amount":30,"unit":"ml"},"display_name":"Amaretto","notes":null},{"ingredient_id":"ING_COFFEE_LIQUEUR","quantity":{"amount":30,"unit":"ml"},"display_name":"Coffee liqueur (Kahlúa)","notes":null},{"ingredient_id":"ING_HEAVY_CREAM","quantity":{"amount":30,"unit":"ml"},"display_name":"Heavy cream","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake well for 10-15 seconds until chilled and frothy. Strain into an ice-filled rocks glass or serve up in a coupe glass. Can also be built directly in glass over ice and stirred."}'::jsonb, 'Rocks glass (or Coupe)', '{"en":"Toasted sliced almonds (optional)"}'::jsonb, 'cubes', 'on_the_rocks', '{"ING_AMARETTO"}', '{"creamy","sweet","nutty","coffee"}', 14, 280, 'easy', 45, '{"milk","tree_nuts"}', '{"lactose","alcohol","caffeine","nut_allergy"}', '{"vegetarian","pescatarian","gluten_free"}', 0, '{"vegetarian","gluten-free"}', '{"all-seasons"}', '{"digestivo","casual","dessert","nightcap"}', false, false, '{"toasted-almond-shooter","frozen-toasted-almond","italian-toasted-almond"}', 'Simple three-ingredient recipe. Can be served on rocks or up depending on preference. Some recipes add vodka for extra strength. Contains nut liqueur - always check for allergies. Popular as both a cocktail and a shooter.', 'low', 65, 'https://www.liquor.com/recipes/toasted-almond/', 'Popular creamy cocktail from the 1970s American bar scene.', 1)
