-- 225. Vodka Tonic


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('1daf6ddb-13df-4c51-8b26-f92e0dccaaad', 'vodka-tonic', 'f1a2b3c4d5e6f7a8b9c0d1e2f3a4b5c6d7e8f9a0', '{"en":"Vodka Tonic"}'::jsonb, 'famous', NULL, '{"highball","long-drink","famous","vodka","tonic","refreshing","simple"}', '{"en":"A crisp, refreshing highball combining vodka with tonic water and lime. The Vodka Tonic is the vodka lover''s answer to the Gin & Tonic, offering a cleaner, more neutral base that lets the tonic''s botanicals shine."}'::jsonb, '{"created_year":"1950","origin":{"city":"United States","country":"USA"},"story":{"en":"The Vodka Tonic emerged in the 1950s as vodka gained popularity in America. While the Gin & Tonic had been popular since the 19th century for its quinine content (used to prevent malaria), the vodka version offered a less botanical, more neutral alternative. The drink became extremely popular in the 1960s and 1970s during the vodka boom, appealing to those who wanted the refreshing qualities of tonic without gin''s distinctive juniper flavor. Today, with the craft tonic water renaissance, the Vodka Tonic has gained new appreciation."},"named_after":{"en":"Simply named after its two main ingredients: vodka and tonic water."}}'::jsonb, '{"profile":["bitter","refreshing","crisp","citrus"],"description":{"en":"Clean and crisp with the tonic''s characteristic bitterness from quinine. The vodka provides a neutral backbone that allows the tonic''s botanicals to shine, while lime adds bright citrus notes. More straightforward and less complex than a Gin & Tonic."},"first_impression":{"en":"Crisp tonic bitterness with clean vodka and bright lime"},"finish":{"en":"Clean, refreshing finish with lingering quinine bitterness"},"balance":{"en":"Well-balanced between vodka neutrality and tonic bitterness, with lime brightening the whole drink"}}'::jsonb, '{"best_time":["afternoon","evening","happy_hour"],"occasions":["casual","social","aperitivo","summer"],"seasons":["spring","summer"],"food_pairings":{"en":"Pairs well with light appetizers, seafood, sushi, salads, and Mediterranean dishes. Excellent with oysters, ceviche, and light cheese plates."},"ideal_for":{"en":"Perfect for those who enjoy the refreshing bitterness of tonic but prefer vodka''s neutrality over gin''s botanical character. Great for warm weather, aperitivo hour, or anyone seeking a clean, crisp drink."}}'::jsonb, '[{"ingredient_id":"ING_VODKA","quantity":{"amount":50,"unit":"ml"},"display_name":"Vodka","notes":null},{"ingredient_id":"ING_TONIC_WATER","quantity":{"amount":100,"unit":"ml"},"display_name":"Tonic water","notes":null},{"ingredient_id":"ING_LIME","quantity":{"amount":1,"unit":"wedge"},"display_name":"Lime wedge","notes":null}]'::jsonb, 'build', '{"en":"Fill a highball glass with ice. Pour vodka over ice. Top with tonic water. Squeeze lime wedge over drink and drop it in. Stir gently once."}'::jsonb, 'Highball glass', '{"en":"Lime wedge"}'::jsonb, 'cubed', 'on_the_rocks', '{"ING_VODKA"}', '{"bitter","refreshing","crisp","citrus"}', 10, 120, 'easy', 20, '{"sulphites"}', '{"alcohol","sulphites_intolerance","quinine_sensitivity"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"casual","social","aperitivo"}', false, false, '{"gin-and-tonic","vodka-soda"}', 'Quality of tonic water is crucial - premium tonics like Fever-Tree make a huge difference. Use fresh lime, not bottled juice. Ratio is flexible - typically 1:2 vodka to tonic.', 'low', 85, 'https://www.diffordsguide.com/cocktails/recipe/1247/vodka-tonic', 'Popular variation of Gin & Tonic, emerged during 1950s vodka boom.', 1)
