-- 228. Washington Apple


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('b871ea95-8b3b-4b76-a519-8582016b471f', 'washington-apple', 'washington_apple_shot_famous_whiskey_apple', '{"en":"Washington Apple"}'::jsonb, 'famous', NULL, '{"shot","shooter","famous","whiskey","apple","fruity","sweet"}', '{"en":"A sweet and fruity shooter combining Canadian whisky, sour apple schnapps, and cranberry juice. Named after Washington State, famous for its apple production, this shot delivers a perfect blend of whiskey warmth and tart apple flavor."}'::jsonb, '{"created_year":"1990s","origin":{"city":"Unknown","bar":"Unknown","country":"USA"},"story":{"en":"The Washington Apple shot gained popularity in the 1990s, likely created as a tribute to Washington State''s famous apple orchards. The combination of Crown Royal whisky with apple schnapps created a uniquely smooth and approachable shooter that became a bar staple across North America."},"named_after":{"en":"Named after Washington State, one of the largest apple-producing regions in the United States."}}'::jsonb, '{"profile":["sweet","fruity","smooth"],"description":{"en":"Smooth whiskey base with bright green apple tartness and a hint of cranberry. Sweet and approachable with just enough tartness to keep it interesting. The Crown Royal adds warmth and depth."},"first_impression":{"en":"Tart green apple with sweet whiskey undertones"},"finish":{"en":"Smooth, warming finish with lingering apple and cranberry notes"},"balance":{"en":"Well-balanced between sweet and tart with smooth whiskey warmth"}}'::jsonb, '{"best_time":["evening","late_night"],"occasions":["party","celebration","nightlife","casual"],"seasons":["autumn","winter"],"food_pairings":{"en":"Pairs well with cheese platters, caramel desserts, or autumn-themed appetizers. Complements pork dishes and roasted nuts."},"ideal_for":{"en":"Perfect for whiskey drinkers who enjoy fruity, approachable shots. A great introduction to whiskey-based shooters for those who prefer sweeter drinks."}}'::jsonb, '[{"ingredient_id":"ING_CANADIAN_WHISKY","quantity":{"amount":20,"unit":"ml"},"display_name":"Canadian Whisky (Crown Royal)","notes":null},{"ingredient_id":"ING_SOUR_APPLE_SCHNAPPS","quantity":{"amount":20,"unit":"ml"},"display_name":"Sour Apple Schnapps","notes":null},{"ingredient_id":"ING_CRANBERRY_JUICE","quantity":{"amount":20,"unit":"ml"},"display_name":"Cranberry Juice","notes":null}]'::jsonb, 'shake', '{"en":"Add all ingredients to a cocktail shaker filled with ice. Shake well until chilled. Strain into a shot glass and serve immediately."}'::jsonb, 'Shot glass', '{"en":"Apple slice (optional)"}'::jsonb, 'none', 'shot', '{"ING_CANADIAN_WHISKY"}', '{"sweet","fruity","smooth"}', 22, 95, 'easy', 45, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","vegetarian","gluten-free","dairy-free"}', '{"autumn","winter"}', '{"party","celebration","nightlife","casual"}', false, false, '{"washington-apple-martini","canadian-apple","crown-apple-shot"}', 'Crown Royal is the traditional choice for this shot, but any Canadian whisky works. DeKuyper Sour Apple Pucker is the most common schnapps used. Can be served as a cocktail over ice in a rocks glass.', 'low', 75, 'https://www.liquor.com/recipes/washington-apple-shot/', 'Popular 1990s shooter featuring Crown Royal and apple schnapps.', 1)
