-- 229. White Negroni


INSERT INTO cocktails (id, slug, stable_key, name, status, iba_category, tags, description, history, taste, recommendations, ingredients, method, instructions, glass, garnish, ice, serving_style, base_spirits, flavor_profile, abv_estimate, calories_estimate, difficulty, prep_time_seconds, computed_allergens, computed_intolerances, computed_diets, computed_spice_level, diet_tags, season_tags, occasion_tags, is_mocktail, is_signature, variants, notes_for_staff, price_tier, popularity, source_url, source_note, version)
VALUES ('64ba6623-afc1-4018-98e9-c1518114dcf1', 'white-negroni', '7a8b9c0d1e2f3a4b5c6d7e8f9a0b1c2d3e4f5a09', '{"en":"White Negroni"}'::jsonb, 'famous', NULL, '{"modern","classic","famous","gin","bitter","aperitivo"}', '{"en":"A pale, elegant variation on the classic Negroni featuring gin, Suze (or gentian liqueur), and Lillet Blanc. The White Negroni offers a more subtle, floral bitterness compared to its red predecessor."}'::jsonb, '{"created_year":"2001","origin":{"city":"Paris","bar":"ECC Chiado","country":"France"},"creator":{"name":"Wayne Collins","profession":"bartender"},"story":{"en":"Created by bartender Wayne Collins in 2001 while working at ECC Chiado in Paris, the White Negroni was born during a Negroni Week event. Collins substituted Suze (a French gentian liqueur) for Campari and Lillet Blanc for sweet vermouth, creating a lighter, more elegant interpretation. The drink gained international recognition and became one of the most popular Negroni variations of the modern era."},"named_after":{"en":"Named \"White Negroni\" for its pale color, distinguishing it from the classic red Negroni."}}'::jsonb, '{"profile":["bitter","floral","herbal"],"description":{"en":"Refined and subtly bitter with floral complexity. Gin provides botanical backbone, Suze contributes elegant gentian bitterness, and Lillet Blanc adds floral sweetness and citrus notes. More delicate than the classic Negroni but equally sophisticated."},"first_impression":{"en":"Gentle bitterness and floral notes hit first, followed by gin botanicals and citrus"},"finish":{"en":"Clean, bitter finish with lingering floral and herbal notes"},"balance":{"en":"Perfectly balanced between bitter and sweet, more subtle than classic Negroni but equally complex"}}'::jsonb, '{"best_time":["late_afternoon","early_evening"],"occasions":["aperitivo","cocktail_hour","date_night"],"seasons":["spring","summer"],"food_pairings":{"en":"Excellent with light appetizers, seafood, fresh cheeses, and vegetable-based dishes. The gentle bitterness makes it a perfect aperitivo."},"ideal_for":{"en":"Perfect for Negroni lovers seeking a lighter, more delicate variation. Ideal for those who appreciate bitter cocktails with floral complexity."}}'::jsonb, '[{"ingredient_id":"ING_GIN","quantity":{"amount":30,"unit":"ml"},"display_name":"Gin","notes":null},{"ingredient_id":"ING_SUZE","quantity":{"amount":30,"unit":"ml"},"display_name":"Suze (gentian liqueur)","notes":null},{"ingredient_id":"ING_LILLET_BLANC","quantity":{"amount":30,"unit":"ml"},"display_name":"Lillet Blanc","notes":null}]'::jsonb, 'stir', '{"en":"Add all ingredients to a mixing glass filled with ice. Stir until well-chilled, about 30 seconds. Strain into a rocks glass over a large ice cube. Express a lemon twist over the drink and use as garnish."}'::jsonb, 'Rocks glass', '{"en":"Lemon twist"}'::jsonb, 'large_cube', 'on_rocks', '{"ING_GIN"}', '{"bitter","floral","herbal"}', 24, 150, 'easy', 90, '{"sulphites"}', '{"alcohol","sulphites_intolerance"}', '{"vegan","vegetarian","pescatarian","gluten_free","dairy_free","nut_free"}', 0, '{"vegan","gluten-free","dairy-free"}', '{"spring","summer"}', '{"aperitivo","cocktail_hour","date_night"}', false, false, '{"negroni","negroni-sbagliato"}', 'Stir, don''t shake. Can substitute other gentian liqueurs if Suze unavailable. Use a large ice cube to minimize dilution. Express lemon oils over drink before adding twist.', 'mid', 80, 'Created by Wayne Collins, 2001', 'Modern variation on the classic Negroni. One of the most popular Negroni variations.', 1)
